import React, { useState, useEffect, useRef } from 'react';

const BackgroundParticles = () => {
  const [particles, setParticles] = useState([]);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const canvasRef = useRef(null);

  useEffect(() => {
    const newParticles = [];
    for (let i = 0; i < 150; i++) {
      newParticles.push({
        id: i,
        x: Math.random() * window.innerWidth,
        y: Math.random() * window.innerHeight,
        size: Math.random() * 8 + 2,
        color: ['#6c5ce7', '#a363d9', '#fd79a8', '#fdcb6e', '#00b894', '#00cec9', '#ff7675', '#74b9ff', '#a29bfe'][Math.floor(Math.random() * 9)],
        shape: ['circle', 'square', 'star', 'heart'][Math.floor(Math.random() * 4)],
        delay: Math.random() * 10,
        duration: Math.random() * 30 + 10,
        direction: Math.random() > 0.5 ? 1 : -1,
        speed: Math.random() * 2 + 1,
        opacity: Math.random() * 0.5 + 0.1,
      });
    }
    setParticles(newParticles);

    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      particles.forEach((particle) => {
        const dx = mousePosition.x - particle.x;
        const dy = mousePosition.y - particle.y;
        const distance = Math.sqrt(dx * dx + dy * dy);

        if (distance < 100) {
          particle.x += (dx / distance) * particle.speed;
          particle.y += (dy / distance) * particle.speed;
        } else {
          particle.y -= particle.speed;
        }

        if (particle.y < -50) {
          particle.y = canvas.height + 50;
          particle.x = Math.random() * canvas.width;
        }

        ctx.save();
        ctx.globalAlpha = particle.opacity;
        ctx.fillStyle = particle.color;
        ctx.translate(particle.x, particle.y);

        if (particle.shape === 'star') {
          drawStar(ctx, 0, 0, particle.size / 2, particle.size, 5);
        } else if (particle.shape === 'heart') {
          drawHeart(ctx, 0, 0, particle.size);
        } else if (particle.shape === 'square') {
          ctx.fillRect(-particle.size / 2, -particle.size / 2, particle.size, particle.size);
        } else {
          ctx.beginPath();
          ctx.arc(0, 0, particle.size / 2, 0, Math.PI * 2);
          ctx.fill();
        }

        ctx.restore();
      });

      requestAnimationFrame(animate);
    };

    animate();

    const handleResize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [particles, mousePosition]);

  const drawStar = (ctx, cx, cy, spikes, outerRadius, innerRadius) => {
    let rot = Math.PI / 2 * 3;
    let x = cx;
    let y = cy;
    let step = Math.PI / spikes;

    ctx.beginPath();
    ctx.moveTo(cx, cy - outerRadius);
    for (let i = 0; i < spikes; i++) {
      x = cx + Math.cos(rot) * outerRadius;
      y = cy + Math.sin(rot) * outerRadius;
      ctx.lineTo(x, y);
      rot += step;

      x = cx + Math.cos(rot) * innerRadius;
      y = cy + Math.sin(rot) * innerRadius;
      ctx.lineTo(x, y);
      rot += step;
    }
    ctx.lineTo(cx, cy - outerRadius);
    ctx.closePath();
    ctx.fill();
  };

  const drawHeart = (ctx, cx, cy, size) => {
    ctx.beginPath();
    ctx.moveTo(cx, cy + size / 4);
    ctx.quadraticCurveTo(cx, cy, cx - size / 2, cy);
    ctx.quadraticCurveTo(cx - size, cy + size / 4, cx - size, cy + size);
    ctx.quadraticCurveTo(cx - size / 2, cy + size * 1.5, cx, cy + size * 1.5);
    ctx.quadraticCurveTo(cx + size / 2, cy + size * 1.5, cx + size, cy + size);
    ctx.quadraticCurveTo(cx + size, cy + size / 4, cx + size / 2, cy);
    ctx.quadraticCurveTo(cx, cy, cx, cy + size / 4);
    ctx.closePath();
    ctx.fill();
  };

  return (
    <div className="background-particles fixed inset-0 pointer-events-none z-0 overflow-hidden">
      <canvas ref={canvasRef} className="absolute inset-0" />
      <div className="floating-shapes absolute inset-0 pointer-events-none">
        {[...Array(20)].map((_, i) => (
          <div
            key={`shape-${i}`}
            className="shape absolute rounded-full opacity-5"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              width: `${Math.random() * 200 + 100}px`,
              height: `${Math.random() * 200 + 100}px`,
              background: `radial-gradient(circle, ${['#6c5ce7', '#a363d9', '#fd79a8'][Math.floor(Math.random() * 3)]} 0%, transparent 70%)`,
              animationDelay: `${Math.random() * 20}s`,
              animationDuration: `${Math.random() * 40 + 20}s`,
              animation: 'float 40s infinite linear',
            }}
          />
        ))}
      </div>
      <style jsx>{`
        .shape {
          animation: float 40s infinite linear;
        }

        @keyframes float {
          0% {
            transform: translateY(100vh) translateX(0) rotate(0deg) scale(1);
            opacity: 0;
          }
          10% {
            opacity: 0.1;
          }
          50% {
            transform: translateY(50vh) translateX(100px) rotate(180deg) scale(1.2);
          }
          90% {
            opacity: 0.1;
          }
          100% {
            transform: translateY(-100vh) translateX(200px) rotate(360deg) scale(0.8);
            opacity: 0;
          }
        }
      `}</style>
    </div>
  );
};

export default BackgroundParticles;
