import React, { useState } from 'react';
import { useProducts } from '../../context/ProductContext';

const Categories = () => {
  const { categories } = useProducts();
  const [hoveredCategory, setHoveredCategory] = useState(null);

  return (
    <section className="category mb-12">
      <div className="container mx-auto px-4">
        <div className="text-center mb-8">
          <h2 className="text-4xl font-bold text-gray-900 mb-4">
            Shop by <span className="text-transparent bg-clip-text bg-gradient-to-r from-purple-600 to-blue-600">Category</span>
          </h2>
          <p className="text-gray-600 text-lg">Discover amazing products in every category</p>
        </div>

        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-6 gap-6">
          {categories.map((category, index) => (
            <div
              key={category.id}
              className="group relative"
              onMouseEnter={() => setHoveredCategory(category.id)}
              onMouseLeave={() => setHoveredCategory(null)}
              style={{ animationDelay: `${index * 0.1}s` }}
            >
              <div className="relative bg-white border-2 border-gray-100 rounded-2xl p-6 text-center transition-all duration-500 hover:border-purple-300 hover:shadow-2xl hover:-translate-y-3 overflow-hidden">
                {/* Background Pattern */}
                <div className="absolute inset-0 bg-gradient-to-br from-purple-50 to-blue-50 opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>

                {/* Floating Elements */}
                {hoveredCategory === category.id && (
                  <>
                    <div className="absolute top-4 right-4 w-3 h-3 bg-yellow-400 rounded-full animate-ping opacity-60"></div>
                    <div className="absolute bottom-4 left-4 w-2 h-2 bg-pink-400 rounded-full animate-pulse opacity-60"></div>
                    <div className="absolute top-1/2 left-2 w-1 h-1 bg-blue-400 rounded-full animate-bounce opacity-60"></div>
                  </>
                )}

                {/* Icon Container */}
                <div className="relative mb-4">
                  <div className="w-20 h-20 bg-gradient-to-br from-gray-50 to-gray-100 border-2 border-gray-200 rounded-2xl flex items-center justify-center mx-auto transition-all duration-500 group-hover:border-purple-300 group-hover:shadow-lg group-hover:scale-110">
                    <img
                      src={category.icon}
                      alt={category.name}
                      className="w-10 h-10 text-gray-700 transition-all duration-500 group-hover:brightness-110 group-hover:drop-shadow-lg"
                    />
                  </div>

                  {/* Ripple Effect */}
                  <div className="absolute inset-0 rounded-2xl opacity-0 group-hover:opacity-100 transition-opacity duration-500">
                    <div className="absolute inset-0 bg-gradient-to-r from-purple-500/20 to-blue-500/20 rounded-2xl animate-ping"></div>
                  </div>
                </div>

                {/* Content */}
                <div className="relative z-10">
                  <h3 className="text-gray-900 text-lg font-bold uppercase mb-2 transition-colors duration-300 group-hover:text-purple-600">
                    {category.name}
                  </h3>
                  <p className="text-gray-500 text-sm font-medium transition-colors duration-300 group-hover:text-gray-700">
                    {category.itemCount} items
                  </p>

                  {/* Progress Bar */}
                  <div className="mt-3 w-full bg-gray-200 rounded-full h-1.5 overflow-hidden">
                    <div
                      className="h-full bg-gradient-to-r from-purple-500 to-blue-500 rounded-full transition-all duration-1000 ease-out"
                      style={{
                        width: hoveredCategory === category.id ? '100%' : '0%'
                      }}
                    ></div>
                  </div>
                </div>

                {/* Hover Arrow */}
                <div className="absolute bottom-4 right-4 opacity-0 group-hover:opacity-100 transition-all duration-300 transform translate-x-2 group-hover:translate-x-0">
                  <div className="w-8 h-8 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full flex items-center justify-center shadow-lg">
                    <i className="fas fa-arrow-right text-white text-sm transition-transform duration-300 group-hover:translate-x-1"></i>
                  </div>
                </div>

                {/* Glow Effect */}
                <div className="absolute inset-0 bg-gradient-to-r from-purple-500/10 to-blue-500/10 opacity-0 group-hover:opacity-100 transition-opacity duration-500 pointer-events-none rounded-2xl blur-xl"></div>
              </div>
            </div>
          ))}
        </div>

        {/* View All Button */}
        <div className="text-center mt-10">
          <button className="group relative bg-gradient-to-r from-purple-600 to-blue-600 text-white font-bold text-lg px-8 py-4 rounded-full hover:from-purple-700 hover:to-blue-700 transition-all duration-300 transform hover:scale-105 hover:shadow-2xl">
            <span className="relative z-10 flex items-center gap-2">
              View All Categories
              <i className="fas fa-arrow-right transition-transform duration-300 group-hover:translate-x-1"></i>
            </span>
            <div className="absolute inset-0 bg-gradient-to-r from-purple-700 to-blue-700 rounded-full opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
          </button>
        </div>
      </div>
    </section>
  );
};

export default Categories;
