import React, { useState, useEffect } from 'react';

const Banner = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isAutoPlaying, setIsAutoPlaying] = useState(true);

  const slides = [
    {
      id: 1,
      image: "/images/banner-1.jpg",
      subtitle: "New Collection",
      title: "Summer Fashion",
      text: "Discover the latest trends in summer fashion with our exclusive collection. Up to 50% off on selected items.",
      buttonText: "Shop Now",
      buttonLink: "/shop",
      gradient: "from-purple-500 to-pink-500"
    },
    {
      id: 2,
      image: "/images/banner-2.jpg",
      subtitle: "Special Offer",
      title: "Electronics Sale",
      text: "Upgrade your tech with our amazing electronics sale. Premium quality at unbeatable prices.",
      buttonText: "Explore",
      buttonLink: "/electronics",
      gradient: "from-blue-500 to-teal-500"
    },
    {
      id: 3,
      image: "/images/banner-3.jpg",
      subtitle: "Limited Time",
      title: "Sports & Fitness",
      text: "Gear up for your fitness journey with our premium sports equipment and activewear collection.",
      buttonText: "Get Started",
      buttonLink: "/sports",
      gradient: "from-green-500 to-blue-500"
    }
  ];

  // Auto-slide functionality
  useEffect(() => {
    if (!isAutoPlaying) return;

    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);

    return () => clearInterval(timer);
  }, [slides.length, isAutoPlaying]);

  const goToSlide = (index) => {
    setCurrentSlide(index);
    setIsAutoPlaying(false);
    // Resume auto-play after 10 seconds
    setTimeout(() => setIsAutoPlaying(true), 10000);
  };

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
    setIsAutoPlaying(false);
    setTimeout(() => setIsAutoPlaying(true), 10000);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
    setIsAutoPlaying(false);
    setTimeout(() => setIsAutoPlaying(true), 10000);
  };

  return (
    <section className="banner mt-8 mb-8 relative overflow-hidden">
      <div className="container mx-auto px-4">
        <div className="relative overflow-hidden rounded-2xl shadow-2xl group">
          {/* Background Pattern */}
          <div className="absolute inset-0 bg-gradient-to-br from-purple-900/20 to-blue-900/20 z-10"></div>

          <div
            className="flex transition-transform duration-700 ease-in-out"
            style={{ transform: `translateX(-${currentSlide * 100}%)` }}
          >
            {slides.map((slide, index) => (
              <div key={slide.id} className="min-w-full relative">
                <div className="relative h-96 md:h-[450px] overflow-hidden">
                  <img
                    src={slide.image}
                    alt={slide.title}
                    className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-110"
                  />

                  {/* Overlay with Gradient */}
                  <div className={`absolute inset-0 bg-gradient-to-r ${slide.gradient} opacity-80`}></div>

                  {/* Animated Overlay Pattern */}
                  <div className="absolute inset-0 opacity-20">
                    <div className="absolute top-10 left-10 w-32 h-32 bg-white rounded-full animate-pulse"></div>
                    <div className="absolute top-32 right-20 w-24 h-24 bg-yellow-300 rounded-full animate-bounce" style={{animationDelay: '1s'}}></div>
                    <div className="absolute bottom-20 left-1/4 w-16 h-16 bg-pink-300 rounded-full animate-pulse" style={{animationDelay: '2s'}}></div>
                  </div>

                  <div className="absolute inset-0 bg-black bg-opacity-40 flex items-center">
                    <div className="container mx-auto px-4 relative z-20">
                      <div className="max-w-lg text-white transform translate-x-0 transition-all duration-700">
                        <p className="text-yellow-300 text-fs-7 font-weight-500 uppercase tracking-wider mb-2.5 animate-fadeInUp">
                          {slide.subtitle}
                        </p>
                        <h1 className="text-fs-1 font-weight-700 uppercase mb-2.5 leading-tight animate-fadeInUp" style={{animationDelay: '0.2s'}}>
                          {slide.title}
                        </h1>
                        <p className="text-fs-7 font-weight-500 mb-5 hidden md:block animate-fadeInUp" style={{animationDelay: '0.4s'}}>
                          {slide.text}
                        </p>
                        <a
                          href={slide.buttonLink}
                          className="inline-block bg-white text-gray-800 text-fs-11 font-weight-600 uppercase px-6 py-3 rounded-border-radius-md hover:bg-yellow-300 transition-all duration-300 transform hover:scale-105 hover:shadow-2xl animate-fadeInUp group"
                          style={{animationDelay: '0.6s'}}
                        >
                          <span className="relative z-10">{slide.buttonText}</span>
                          <div className="absolute inset-0 bg-gradient-to-r from-yellow-300 to-orange-300 rounded-border-radius-md opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                        </a>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Enhanced Navigation Dots */}
          <div className="absolute bottom-6 left-1/2 transform -translate-x-1/2 flex gap-3 z-30">
            {slides.map((_, index) => (
              <button
                key={index}
                onClick={() => goToSlide(index)}
                className={`relative w-4 h-4 rounded-full transition-all duration-300 transform hover:scale-125 ${
                  index === currentSlide
                    ? 'bg-white shadow-lg scale-125'
                    : 'bg-white bg-opacity-50 hover:bg-opacity-75'
                }`}
              >
                {index === currentSlide && (
                  <div className="absolute inset-0 bg-white rounded-full animate-ping opacity-75"></div>
                )}
              </button>
            ))}
          </div>

          {/* Enhanced Navigation Arrows */}
          <button
            onClick={prevSlide}
            className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-white bg-opacity-20 text-white p-3 rounded-full hover:bg-opacity-40 transition-all duration-300 z-30 backdrop-blur-sm hover:scale-110 hover:shadow-xl"
          >
            <i className="fas fa-chevron-left text-lg"></i>
          </button>
          <button
            onClick={nextSlide}
            className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-white bg-opacity-20 text-white p-3 rounded-full hover:bg-opacity-40 transition-all duration-300 z-30 backdrop-blur-sm hover:scale-110 hover:shadow-xl"
          >
            <i className="fas fa-chevron-right text-lg"></i>
          </button>

          {/* Slide Counter */}
          <div className="absolute top-6 right-6 bg-black bg-opacity-50 text-white px-3 py-1 rounded-full text-sm z-30 backdrop-blur-sm">
            {currentSlide + 1} / {slides.length}
          </div>

          {/* Progress Bar */}
          <div className="absolute bottom-0 left-0 w-full h-1 bg-black bg-opacity-30 z-30">
            <div
              className="h-full bg-gradient-to-r from-white to-yellow-300 transition-all duration-300 ease-linear"
              style={{width: `${((currentSlide + 1) / slides.length) * 100}%`}}
            ></div>
          </div>
        </div>
      </div>

      <style jsx>{`
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .animate-fadeInUp {
          animation: fadeInUp 0.8s ease-out forwards;
          opacity: 0;
        }

        /* Enhanced floating elements */
        @keyframes float {
          0%, 100% {
            transform: translateY(0px) rotate(0deg);
          }
          50% {
            transform: translateY(-20px) rotate(5deg);
          }
        }

        .banner .absolute.animate-pulse {
          animation: float 6s ease-in-out infinite;
        }

        .banner .absolute.animate-bounce {
          animation: float 4s ease-in-out infinite;
        }

        /* Button hover effects */
        .banner a:hover {
          filter: brightness(1.1);
          box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
        }

        /* Image zoom effect on hover */
        .banner .group:hover img {
          transform: scale(1.1);
        }

        /* Progress bar animation */
        .progress-bar {
          background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
          animation: shimmer 2s infinite;
        }

        @keyframes shimmer {
          0% { transform: translateX(-100%); }
          100% { transform: translateX(100%); }
        }

        /* Enhanced backdrop blur */
        .backdrop-blur-sm {
          backdrop-filter: blur(4px);
        }

        /* Slide transition enhancement */
        .banner .flex {
          will-change: transform;
        }

        /* Mobile responsive adjustments */
        @media (max-width: 768px) {
          .banner .container .max-w-lg {
            max-width: 90%;
          }

          .banner h1 {
            font-size: 2rem;
            line-height: 1.2;
          }

          .banner .absolute.bottom-6 {
            bottom: 4px;
          }

          .banner .absolute.top-6 {
            top: 4px;
          }

          .banner .absolute.left-4,
          .banner .absolute.right-4 {
            display: none;
          }
        }
      `}</style>
    </section>
  );
};

export default Banner;
