import React, { useState } from 'react';
import { useProducts } from '../../context/ProductContext';

const Sidebar = () => {
  const { categories, updateFilters } = useProducts();
  const [isOpen, setIsOpen] = useState(false);
  const [activeFilter, setActiveFilter] = useState('');

  const handleCategoryFilter = (category) => {
    updateFilters({ category: category === 'all' ? '' : category });
    setActiveFilter(category);
  };

  const handlePriceFilter = (priceRange) => {
    updateFilters({ priceRange });
    setActiveFilter(priceRange);
  };

  const handleRatingFilter = (rating) => {
    updateFilters({ rating });
    setActiveFilter(rating.toString());
  };

  return (
    <aside className="w-full lg:w-80 flex-shrink-0">
      <div className="lg:sticky lg:top-8">
        {/* Sidebar Category with Enhanced Design */}
        <div className="bg-white border-2 border-gray-100 rounded-2xl p-6 mb-6 shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1">
          <h3 className="text-gray-800 text-lg font-bold uppercase tracking-wider mb-4 relative">
            Categories
            <div className="absolute -bottom-1 left-0 w-12 h-0.5 bg-gradient-to-r from-purple-500 to-blue-500"></div>
          </h3>

          <ul className="space-y-3">
            <li>
              <button
                onClick={() => handleCategoryFilter('all')}
                className={`flex items-center justify-between w-full text-gray-600 text-base hover:text-purple-600 transition-all duration-300 transform hover:translate-x-2 hover:scale-105 p-2 rounded-lg ${
                  activeFilter === 'all' ? 'bg-purple-100 text-purple-600 border-l-4 border-purple-500' : 'hover:bg-gray-50'
                }`}
              >
                <span className="flex items-center gap-2">
                  <i className="fas fa-th-large text-sm"></i>
                  All Categories
                </span>
                <i className="fas fa-chevron-right text-sm opacity-0 group-hover:opacity-100 transition-opacity duration-300"></i>
              </button>
            </li>
            {categories.map((category, index) => (
              <li key={category.id}>
                <button
                  onClick={() => handleCategoryFilter(category.name.toLowerCase())}
                  className={`group flex items-center justify-between w-full text-gray-600 text-base hover:text-purple-600 transition-all duration-300 transform hover:translate-x-2 hover:scale-105 p-2 rounded-lg ${
                    activeFilter === category.name.toLowerCase() ? 'bg-purple-100 text-purple-600 border-l-4 border-purple-500' : 'hover:bg-gray-50'
                  }`}
                  style={{animationDelay: `${index * 0.1}s`}}
                >
                  <span className="flex items-center gap-2 capitalize">
                    <i className="fas fa-tag text-sm opacity-0 group-hover:opacity-100 transition-opacity duration-300"></i>
                    {category.name}
                  </span>
                  <span className="text-xs bg-gray-200 text-gray-600 px-2 py-1 rounded-full group-hover:bg-purple-200 group-hover:text-purple-600 transition-all duration-300">
                    ({category.itemCount})
                  </span>
                </button>
              </li>
            ))}
          </ul>
        </div>

        {/* Price Filter with Advanced Effects */}
        <div className="bg-white border-2 border-gray-100 rounded-2xl p-6 mb-6 shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1">
          <h3 className="text-gray-800 text-lg font-bold uppercase tracking-wider mb-4 relative">
            Price Range
            <div className="absolute -bottom-1 left-0 w-16 h-0.5 bg-gradient-to-r from-green-500 to-teal-500"></div>
          </h3>

          <ul className="space-y-3">
            <li>
              <button
                onClick={() => handlePriceFilter('')}
                className={`flex items-center justify-between w-full text-gray-600 text-base hover:text-green-600 transition-all duration-300 transform hover:translate-x-2 hover:scale-105 p-2 rounded-lg ${
                  activeFilter === '' ? 'bg-green-100 text-green-600 border-l-4 border-green-500' : 'hover:bg-gray-50'
                }`}
              >
                <span className="flex items-center gap-2">
                  <i className="fas fa-dollar-sign text-sm"></i>
                  All Prices
                </span>
              </button>
            </li>
            {[
              { range: '0-50', label: '$0 - $50' },
              { range: '50-100', label: '$50 - $100' },
              { range: '100-200', label: '$100 - $200' },
              { range: '200-500', label: '$200+' }
            ].map((price, index) => (
              <li key={price.range}>
                <button
                  onClick={() => handlePriceFilter(price.range)}
                  className={`group flex items-center justify-between w-full text-gray-600 text-base hover:text-green-600 transition-all duration-300 transform hover:translate-x-2 hover:scale-105 p-2 rounded-lg ${
                    activeFilter === price.range ? 'bg-green-100 text-green-600 border-l-4 border-green-500' : 'hover:bg-gray-50'
                  }`}
                  style={{animationDelay: `${index * 0.1}s`}}
                >
                  <span className="flex items-center gap-2">
                    <i className="fas fa-tag text-sm opacity-0 group-hover:opacity-100 transition-opacity duration-300"></i>
                    {price.label}
                  </span>
                  <i className="fas fa-chevron-right text-sm opacity-0 group-hover:opacity-100 transition-opacity duration-300"></i>
                </button>
              </li>
            ))}
          </ul>
        </div>

        {/* Rating Filter with Enhanced Animations */}
        <div className="bg-white border-2 border-gray-100 rounded-2xl p-6 mb-6 shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1">
          <h3 className="text-gray-800 text-lg font-bold uppercase tracking-wider mb-4 relative">
            Customer Rating
            <div className="absolute -bottom-1 left-0 w-20 h-0.5 bg-gradient-to-r from-yellow-500 to-orange-500"></div>
          </h3>

          <ul className="space-y-3">
            {[5, 4, 3, 2, 1].map((rating, index) => (
              <li key={rating}>
                <button
                  onClick={() => handleRatingFilter(rating)}
                  className={`group flex items-center gap-2 text-gray-600 text-base hover:text-yellow-600 transition-all duration-300 transform hover:translate-x-2 hover:scale-105 p-2 rounded-lg w-full text-left ${
                    activeFilter === rating.toString() ? 'bg-yellow-100 text-yellow-600 border-l-4 border-yellow-500' : 'hover:bg-gray-50'
                  }`}
                  style={{animationDelay: `${index * 0.1}s`}}
                >
                  <div className="flex">
                    {[1, 2, 3, 4, 5].map((star) => (
                      <i
                        key={star}
                        className={`fas fa-star text-sm transition-all duration-300 hover:scale-125 ${
                          star <= rating ? 'text-yellow-400 hover:text-yellow-500' : 'text-gray-300 hover:text-yellow-300'
                        }`}
                        style={{ transitionDelay: `${star * 0.1}s` }}
                      ></i>
                    ))}
                  </div>
                  <span className="ml-2">& Up</span>
                  <i className="fas fa-chevron-right text-sm opacity-0 group-hover:opacity-100 transition-opacity duration-300 ml-auto"></i>
                </button>
              </li>
            ))}
          </ul>
        </div>

        {/* Product Showcase with Advanced Effects */}
        <div className="bg-gradient-to-br from-purple-50 to-blue-50 border-2 border-purple-100 rounded-2xl p-6 shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1 relative overflow-hidden">
          <div className="absolute inset-0 bg-gradient-to-r from-purple-400 to-blue-400 opacity-0 hover:opacity-10 transition-opacity duration-500"></div>

          <h3 className="text-gray-800 text-lg font-bold uppercase tracking-wider mb-4 relative">
            Featured Products
            <div className="absolute -bottom-1 left-0 w-24 h-0.5 bg-gradient-to-r from-purple-500 to-pink-500"></div>
          </h3>

          <div className="space-y-4 relative z-10">
            {/* Sample featured products */}
            {[
              { name: 'Premium Wireless Headphones', price: '$299.99', originalPrice: '$399.99', image: '/api/placeholder/70/70' },
              { name: 'Smart Fitness Watch', price: '$199.99', originalPrice: null, image: '/api/placeholder/70/70' }
            ].map((product, index) => (
              <div
                key={index}
                className="flex items-center gap-4 p-3 bg-white rounded-xl hover:bg-gray-50 transition-all duration-300 transform hover:scale-105 hover:shadow-md"
                style={{animationDelay: `${index * 0.2}s`}}
              >
                <div className="relative">
                  <img
                    src={product.image}
                    alt={product.name}
                    className="w-16 h-16 rounded-lg object-cover transition-transform duration-300 hover:scale-110"
                  />
                  <div className="absolute inset-0 bg-gradient-to-r from-purple-400 to-pink-400 rounded-lg opacity-0 hover:opacity-20 transition-opacity duration-300"></div>
                </div>
                <div className="flex-1">
                  <h4 className="text-gray-800 text-base font-medium capitalize mb-1 transition-colors duration-300 hover:text-purple-600">
                    {product.name}
                  </h4>
                  <div className="flex items-center gap-2">
                    <span className="text-purple-600 text-base font-bold">
                      {product.price}
                    </span>
                    {product.originalPrice && (
                      <del className="text-gray-400 text-sm">
                        {product.originalPrice}
                      </del>
                    )}
                  </div>
                </div>
                <button className="text-purple-600 hover:text-white hover:bg-purple-600 p-2 rounded-full transition-all duration-300 transform hover:scale-110">
                  <i className="fas fa-shopping-cart text-sm"></i>
                </button>
              </div>
            ))}
          </div>

          {/* Decorative Elements */}
          <div className="absolute top-4 right-4 w-8 h-8 border-2 border-purple-200 rounded-full opacity-0 hover:opacity-100 transition-all duration-500 transform rotate-45 hover:rotate-0"></div>
          <div className="absolute bottom-4 left-4 w-6 h-6 bg-gradient-to-r from-pink-200 to-purple-200 rounded-full opacity-0 hover:opacity-100 transition-all duration-500 transform scale-0 hover:scale-100"></div>
        </div>
      </div>

      <style jsx>{`
        /* Enhanced animations */
        @keyframes fadeInLeft {
          from {
            opacity: 0;
            transform: translateX(-30px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }

        .bg-white {
          animation: fadeInLeft 0.6s ease-out forwards;
        }

        /* Staggered animation delays */
        .bg-white:nth-child(1) { animation-delay: 0.1s; }
        .bg-white:nth-child(2) { animation-delay: 0.2s; }
        .bg-white:nth-child(3) { animation-delay: 0.3s; }
        .bg-white:nth-child(4) { animation-delay: 0.4s; }

        /* Enhanced hover effects */
        .bg-white:hover {
          box-shadow: 0 20px 40px rgba(147, 51, 234, 0.15);
        }

        /* Button active states */
        button:active {
          transform: scale(0.95);
        }

        /* Icon rotation on hover */
        .group:hover i {
          transform: rotate(360deg);
        }

        /* Floating animation for decorative elements */
        @keyframes float {
          0%, 100% {
            transform: translateY(0px) rotate(0deg);
          }
          50% {
            transform: translateY(-10px) rotate(180deg);
          }
        }

        .absolute.top-4.right-4 {
          animation: float 3s ease-in-out infinite;
        }

        /* Mobile responsive adjustments */
        @media (max-width: 1024px) {
          aside {
            order: 2;
          }

          .lg\\:sticky {
            position: static;
          }
        }

        @media (max-width: 768px) {
          .bg-white {
            margin-bottom: 1rem;
          }

          .bg-white:hover {
            transform: translateY(-5px);
          }
        }

        /* Enhanced scrollbar for webkit browsers */
        ::-webkit-scrollbar {
          width: 6px;
        }

        ::-webkit-scrollbar-track {
          background: #f1f5f9;
          border-radius: 3px;
        }

        ::-webkit-scrollbar-thumb {
          background: linear-gradient(45deg, #7c3aed, #ec4899);
          border-radius: 3px;
        }

        ::-webkit-scrollbar-thumb:hover {
          background: linear-gradient(45deg, #6d28d9, #db2777);
        }
      `}</style>
    </aside>
  );
};

export default Sidebar;
