import React from 'react';
import { useProducts } from '../../context/ProductContext';

const Categories = () => {
  const { categories } = useProducts();

  return (
    <section className="category mb-8">
      <div className="container mx-auto px-4">
        <div className="flex items-center gap-2.5 overflow-x-auto scrollbar-hide">
          {categories.map((category) => (
            <div
              key={category.id}
              className="min-w-[calc(25%-15px)] md:min-w-[calc(20%-16px)] flex flex-col items-center gap-2.5 p-4 border border-cultured rounded-border-radius-md hover:border-salmon-pink transition-colors cursor-pointer group"
            >
              <div className="w-16 h-16 bg-cultured border border-gray-200 rounded-border-radius-sm flex items-center justify-center group-hover:bg-salmon-pink group-hover:border-salmon-pink transition-colors">
                <img
                  src={category.icon}
                  alt={category.name}
                  className="w-8 h-8 text-onyx group-hover:brightness-0 group-hover:invert transition-all"
                />
              </div>
              <div className="text-center">
                <h3 className="text-eerie-black text-fs-9 font-weight-600 uppercase mb-1">
                  {category.name}
                </h3>
                <p className="text-sonic-silver text-fs-11">
                  {category.itemCount} items
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Categories;
