import React, { useEffect } from 'react';
import { useUser } from '../../context/UserContext';

const Modal = () => {
  const { modal, hideModal, resetAnimation } = useUser();

  if (!modal.isOpen) return null;

  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      hideModal();
    }
  };

  const handleNewsletterSubmit = (e) => {
    e.preventDefault();
    // TODO: Implement newsletter subscription
    hideModal();
  };

  return (
    <div className="modal">
      <div className="modal-close-overlay" onClick={handleBackdropClick}></div>

      <div className="modal-content">
        <button
          onClick={hideModal}
          className="modal-close-btn absolute top-4 right-4 bg-salmon-pink text-white text-lg p-1.5 rounded-border-radius-sm hover:opacity-90 z-10"
        >
          <i className="fas fa-times"></i>
        </button>

        <div className="newsletter-img">
          <img src="/images/newsletter.png" alt="subscribe newsletter" width="400" height="400" />
        </div>

        <div className="newsletter">
          <form onSubmit={handleNewsletterSubmit}>
            <div className="newsletter-header">
              <h3 className="newsletter-title text-onyx text-fs-2 font-weight-600 mb-2.5">
                Subscribe Newsletter.
              </h3>
              <p className="newsletter-desc text-sonic-silver text-fs-7 leading-relaxed">
                Subscribe the <b>Anon</b> to get latest products and discount update.
              </p>
            </div>

            <input
              type="email"
              name="email"
              className="email-field text-fs-7 px-4 py-2 border border-cultured rounded-border-radius-sm mb-4"
              placeholder="Email Address"
              required
            />

            <button
              type="submit"
              className="btn-newsletter bg-eerie-black text-white text-fs-7 font-weight-600 uppercase px-4 py-2.5 rounded-border-radius-sm hover:bg-salmon-pink transition-colors"
            >
              Subscribe
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Modal;
