import React from 'react';
import { useProducts } from '../../context/ProductContext';
import { useCart } from '../../context/CartContext';
import { useUser } from '../../context/UserContext';

const ProductGrid = () => {
  const { getFilteredProducts, loading, error } = useProducts();
  const { addToCart } = useCart();
  const { showToast } = useUser();

  const products = getFilteredProducts();

  const handleAddToCart = (product) => {
    addToCart(product.id, 1);
    showToast(`${product.name} added to cart!`, 'success');
  };

  if (loading) {
    return (
      <section className="product-main mb-8">
        <div className="container mx-auto px-4">
          <div className="flex justify-center items-center py-12">
            <div className="text-center">
              <i className="fas fa-spinner fa-spin text-3xl text-salmon-pink mb-4"></i>
              <p className="text-sonic-silver">Loading products...</p>
            </div>
          </div>
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section className="product-main mb-8">
        <div className="container mx-auto px-4">
          <div className="flex justify-center items-center py-12">
            <div className="text-center">
              <i className="fas fa-exclamation-triangle text-3xl text-bittersweet mb-4"></i>
              <p className="text-sonic-silver">Error: {error}</p>
            </div>
          </div>
        </div>
      </section>
    );
  }

  if (products.length === 0) {
    return (
      <section className="product-main mb-8">
        <div className="container mx-auto px-4">
          <div className="flex justify-center items-center py-12">
            <div className="text-center">
              <i className="fas fa-search text-3xl text-sonic-silver mb-4"></i>
              <p className="text-sonic-silver">No products found matching your criteria.</p>
            </div>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section className="product-main mb-8">
      <div className="container mx-auto px-4">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {products.map((product) => (
            <div
              key={product.id}
              className="product-grid group bg-white border border-cultured rounded-border-radius-md overflow-hidden transition-all duration-300 hover:shadow-lg"
            >
              {/* Product Image */}
              <div className="relative overflow-hidden">
                <img
                  src={product.image}
                  alt={product.name}
                  className="w-full h-64 object-cover transition-transform duration-300 group-hover:scale-105"
                />

                {/* Badge */}
                {product.badge && (
                  <div
                    className={`absolute top-4 left-4 px-2 py-1 text-white text-fs-8 font-weight-500 rounded-border-radius-sm ${
                      product.badgeType === 'pink' ? 'bg-salmon-pink' :
                      product.badgeType === 'ocean-green' ? 'bg-ocean-green' :
                      product.badgeType === 'bittersweet' ? 'bg-bittersweet' : 'bg-eerie-black'
                    }`}
                  >
                    {product.badge}
                  </div>
                )}

                {/* Action Buttons */}
                <div className="absolute top-4 right-4 flex flex-col gap-2 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                  <button className="w-10 h-10 bg-white text-sonic-silver border border-cultured rounded-border-radius-sm flex items-center justify-center hover:bg-eerie-black hover:text-white transition-colors duration-300">
                    <i className="fas fa-heart"></i>
                  </button>
                  <button className="w-10 h-10 bg-white text-sonic-silver border border-cultured rounded-border-radius-sm flex items-center justify-center hover:bg-eerie-black hover:text-white transition-colors duration-300">
                    <i className="fas fa-eye"></i>
                  </button>
                </div>
              </div>

              {/* Product Content */}
              <div className="p-5">
                {/* Category */}
                <p className="text-salmon-pink text-fs-9 font-weight-500 uppercase mb-2">
                  {product.category}
                </p>

                {/* Title */}
                <h3 className="text-sonic-silver text-fs-8 font-weight-300 capitalize mb-2 transition-colors duration-300 group-hover:text-eerie-black">
                  {product.name}
                </h3>

                {/* Rating */}
                <div className="flex items-center mb-3">
                  <div className="flex text-sandy-brown">
                    {[1, 2, 3, 4, 5].map((star) => (
                      <i
                        key={star}
                        className={`fas fa-star text-sm ${
                          star <= Math.floor(product.rating) ? 'text-sandy-brown' : 'text-gray-300'
                        }`}
                      ></i>
                    ))}
                  </div>
                  <span className="text-sonic-silver text-fs-9 ml-2">
                    ({product.reviews})
                  </span>
                </div>

                {/* Price */}
                <div className="flex items-center gap-2 mb-4">
                  <span className="text-eerie-black text-fs-7 font-weight-700">
                    ${product.price}
                  </span>
                  {product.originalPrice && (
                    <del className="text-sonic-silver text-fs-9">
                      ${product.originalPrice}
                    </del>
                  )}
                </div>

                {/* Add to Cart Button */}
                <button
                  onClick={() => handleAddToCart(product)}
                  className="w-full bg-salmon-pink text-white text-fs-9 font-weight-700 uppercase py-2.5 rounded-border-radius-sm hover:bg-eerie-black transition-colors duration-300"
                >
                  Add to Cart
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default ProductGrid;
