import React from 'react';
import { useProducts } from '../../context/ProductContext';
import { useCart } from '../../context/CartContext';
import { useUser } from '../../context/UserContext';

const ProductGrid = () => {
  const { getFilteredProducts, loading, error } = useProducts();
  const { addToCart } = useCart();
  const { showToast } = useUser();

  const products = getFilteredProducts();

  const handleAddToCart = (product) => {
    addToCart(product.id, 1);
    showToast(`${product.name} added to cart!`, 'success');
  };

  if (loading) {
    return (
      <section className="product-main mb-8">
        <div className="container mx-auto px-4">
          <div className="flex justify-center items-center py-12">
            <div className="text-center">
              <div className="relative">
                <i className="fas fa-spinner fa-spin text-4xl text-purple-500 mb-4"></i>
                <div className="absolute inset-0 w-16 h-16 border-4 border-purple-200 border-t-purple-500 rounded-full animate-spin mx-auto -top-2"></div>
              </div>
              <p className="text-sonic-silver text-lg">Loading amazing products...</p>
            </div>
          </div>
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section className="product-main mb-8">
        <div className="container mx-auto px-4">
          <div className="flex justify-center items-center py-12">
            <div className="text-center">
              <div className="relative">
                <i className="fas fa-exclamation-triangle text-4xl text-red-500 mb-4 animate-bounce"></i>
                <div className="absolute inset-0 text-red-300 animate-ping">
                  <i className="fas fa-exclamation-triangle text-4xl"></i>
                </div>
              </div>
              <p className="text-sonic-silver text-lg">Error: {error}</p>
            </div>
          </div>
        </div>
      </section>
    );
  }

  if (products.length === 0) {
    return (
      <section className="product-main mb-8">
        <div className="container mx-auto px-4">
          <div className="flex justify-center items-center py-12">
            <div className="text-center">
              <div className="relative">
                <i className="fas fa-search text-4xl text-gray-400 mb-4 animate-pulse"></i>
                <div className="absolute inset-0 text-gray-300 animate-ping">
                  <i className="fas fa-search text-4xl"></i>
                </div>
              </div>
              <p className="text-sonic-silver text-lg">No products found matching your criteria.</p>
            </div>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section className="product-main mb-8">
      <div className="container mx-auto px-4">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {products.map((product, index) => (
            <div
              key={product.id}
              className="product-grid group bg-white border border-cultured rounded-2xl overflow-hidden transition-all duration-500 hover:shadow-2xl hover:-translate-y-2 transform"
              style={{
                animationDelay: `${index * 0.1}s`,
                animation: 'fadeInUp 0.6s ease-out forwards'
              }}
            >
              {/* Product Image with Enhanced Effects */}
              <div className="relative overflow-hidden bg-gradient-to-br from-gray-50 to-gray-100">
                <img
                  src={product.image}
                  alt={product.name}
                  className="w-full h-64 object-cover transition-all duration-700 group-hover:scale-110 group-hover:brightness-110"
                />

                {/* Gradient Overlay */}
                <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>

                {/* Badge with Animation */}
                {product.badge && (
                  <div
                    className={`absolute top-4 left-4 px-3 py-1 text-white text-fs-8 font-weight-500 rounded-full transform transition-all duration-300 hover:scale-110 ${
                      product.badgeType === 'pink' ? 'bg-gradient-to-r from-pink-500 to-red-500 animate-pulse' :
                      product.badgeType === 'ocean-green' ? 'bg-gradient-to-r from-green-500 to-teal-500' :
                      product.badgeType === 'bittersweet' ? 'bg-gradient-to-r from-orange-500 to-red-500' : 'bg-gradient-to-r from-purple-500 to-blue-500'
                    }`}
                  >
                    {product.badge}
                  </div>
                )}

                {/* Action Buttons with Advanced Effects */}
                <div className="absolute top-4 right-4 flex flex-col gap-2 opacity-0 group-hover:opacity-100 transition-all duration-300 transform translate-x-4 group-hover:translate-x-0">
                  <button className="w-10 h-10 bg-white text-gray-700 border border-gray-200 rounded-full flex items-center justify-center hover:bg-gradient-to-r hover:from-red-500 hover:to-pink-500 hover:text-white hover:border-transparent transition-all duration-300 transform hover:scale-110 hover:rotate-12 shadow-lg hover:shadow-xl">
                    <i className="fas fa-heart text-sm"></i>
                  </button>
                  <button className="w-10 h-10 bg-white text-gray-700 border border-gray-200 rounded-full flex items-center justify-center hover:bg-gradient-to-r hover:from-blue-500 hover:to-purple-500 hover:text-white hover:border-transparent transition-all duration-300 transform hover:scale-110 hover:-rotate-12 shadow-lg hover:shadow-xl">
                    <i className="fas fa-eye text-sm"></i>
                  </button>
                </div>

                {/* Quick Add to Cart Button */}
                <div className="absolute bottom-4 left-4 right-4 opacity-0 group-hover:opacity-100 transition-all duration-300 transform translate-y-4 group-hover:translate-y-0">
                  <button
                    onClick={() => handleAddToCart(product)}
                    className="w-full bg-gradient-to-r from-purple-500 to-blue-500 text-white text-fs-9 font-weight-600 py-2 rounded-lg hover:from-purple-600 hover:to-blue-600 transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl"
                  >
                    Quick Add
                  </button>
                </div>

                {/* Floating Elements */}
                <div className="absolute inset-0 opacity-0 group-hover:opacity-20 transition-opacity duration-500">
                  <div className="absolute top-1/4 left-1/4 w-2 h-2 bg-yellow-400 rounded-full animate-ping"></div>
                  <div className="absolute top-3/4 right-1/4 w-3 h-3 bg-pink-400 rounded-full animate-pulse" style={{animationDelay: '0.5s'}}></div>
                  <div className="absolute bottom-1/4 left-1/2 w-2 h-2 bg-blue-400 rounded-full animate-bounce" style={{animationDelay: '1s'}}></div>
                </div>
              </div>

              {/* Product Content with Enhanced Animations */}
              <div className="p-5 relative">
                {/* Category with Animation */}
                <p className="text-purple-600 text-fs-9 font-weight-500 uppercase mb-2 transform transition-all duration-300 group-hover:scale-105">
                  {product.category}
                </p>

                {/* Title with Hover Effect */}
                <h3 className="text-gray-800 text-fs-8 font-weight-500 capitalize mb-2 transition-all duration-300 group-hover:text-purple-600 transform group-hover:translate-x-1">
                  {product.name}
                </h3>

                {/* Rating with Enhanced Animation */}
                <div className="flex items-center mb-3">
                  <div className="flex text-yellow-400 mr-2">
                    {[1, 2, 3, 4, 5].map((star) => (
                      <i
                        key={star}
                        className={`fas fa-star text-sm transition-all duration-300 hover:scale-125 ${
                          star <= Math.floor(product.rating) ? 'text-yellow-400' : 'text-gray-300 hover:text-yellow-300'
                        }`}
                        style={{ transitionDelay: `${star * 0.1}s` }}
                      ></i>
                    ))}
                  </div>
                  <span className="text-gray-500 text-fs-9 transition-colors duration-300 group-hover:text-gray-700">
                    ({product.reviews})
                  </span>
                </div>

                {/* Price with Animation */}
                <div className="flex items-center gap-2 mb-4">
                  <span className="text-gray-900 text-fs-7 font-weight-700 transition-all duration-300 group-hover:scale-110">
                    ${product.price}
                  </span>
                  {product.originalPrice && (
                    <del className="text-gray-400 text-fs-9 transition-all duration-300 group-hover:scale-105">
                      ${product.originalPrice}
                    </del>
                  )}
                </div>

                {/* Add to Cart Button with Advanced Effects */}
                <button
                  onClick={() => handleAddToCart(product)}
                  className="w-full bg-gradient-to-r from-purple-500 to-blue-500 text-white text-fs-9 font-weight-600 uppercase py-3 rounded-lg hover:from-purple-600 hover:to-blue-600 transition-all duration-300 transform hover:scale-105 hover:shadow-xl relative overflow-hidden group/btn"
                >
                  <span className="relative z-10 flex items-center justify-center gap-2">
                    <i className="fas fa-shopping-cart group-hover/btn:animate-bounce"></i>
                    Add to Cart
                  </span>
                  <div className="absolute inset-0 bg-gradient-to-r from-blue-500 to-purple-500 opacity-0 group-hover/btn:opacity-100 transition-opacity duration-300"></div>
                </button>

                {/* Decorative Elements */}
                <div className="absolute top-2 right-2 w-8 h-8 border-2 border-purple-200 rounded-full opacity-0 group-hover:opacity-100 transition-all duration-500 transform rotate-45 group-hover:rotate-0"></div>
                <div className="absolute bottom-2 left-2 w-6 h-6 bg-gradient-to-r from-pink-200 to-purple-200 rounded-full opacity-0 group-hover:opacity-100 transition-all duration-500 transform scale-0 group-hover:scale-100"></div>
              </div>
            </div>
          ))}
        </div>
      </div>

      <style jsx>{`
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .product-grid {
          opacity: 0;
          animation: fadeInUp 0.6s ease-out forwards;
        }

        /* Enhanced hover effects */
        .product-grid:hover {
          box-shadow: 0 25px 50px rgba(0, 0, 0, 0.15);
        }

        .product-grid:hover img {
          filter: brightness(1.1) contrast(1.1);
        }

        /* Button ripple effect */
        .product-grid button:active {
          transform: scale(0.95);
        }

        /* Floating animation for decorative elements */
        @keyframes float {
          0%, 100% {
            transform: translateY(0px) rotate(0deg);
          }
          50% {
            transform: translateY(-10px) rotate(180deg);
          }
        }

        .product-grid:hover .absolute.top-2.right-2 {
          animation: float 2s ease-in-out infinite;
        }

        /* Pulse animation for badges */
        .product-grid .bg-gradient-to-r {
          animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
        }

        /* Enhanced loading spinner */
        .fa-spinner {
          animation: spin 1s linear infinite;
        }

        @keyframes spin {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }

        /* Mobile responsive adjustments */
        @media (max-width: 768px) {
          .product-grid {
            margin-bottom: 1rem;
          }

          .product-grid:hover {
            transform: translateY(-5px);
          }

          .product-grid .absolute.right-4 {
            right: 2px;
          }

          .product-grid .absolute.left-4 {
            left: 2px;
          }
        }

        /* Staggered animation delays */
        .product-grid:nth-child(1) { animation-delay: 0.1s; }
        .product-grid:nth-child(2) { animation-delay: 0.2s; }
        .product-grid:nth-child(3) { animation-delay: 0.3s; }
        .product-grid:nth-child(4) { animation-delay: 0.4s; }
        .product-grid:nth-child(5) { animation-delay: 0.5s; }
        .product-grid:nth-child(6) { animation-delay: 0.6s; }
        .product-grid:nth-child(7) { animation-delay: 0.7s; }
        .product-grid:nth-child(8) { animation-delay: 0.8s; }
      `}</style>
    </section>
  );
};

export default ProductGrid;
