import React, { useState } from 'react';
import { useUser } from '../../context/UserContext';
import { useCart } from '../../context/CartContext';

const Header = () => {
  const { toggleMobileMenu, closeMobileMenu } = useUser();
  const { itemCount } = useCart();
  const [searchQuery, setSearchQuery] = useState('');
  const [isSearchFocused, setIsSearchFocused] = useState(false);

  const handleSearch = (e) => {
    e.preventDefault();
    // TODO: Implement search functionality
    console.log('Search query:', searchQuery);
  };

  return (
    <>
      {/* Header Top */}
      <div className="bg-white border-b border-cultured py-2.5 hidden md:block">
        <div className="container mx-auto px-4">
          <div className="flex justify-center items-center">
            <p className="text-sonic-silver text-fs-9 uppercase">
              Free shipping on orders over $50 | <span className="font-weight-500">Get 20% off</span>
            </p>
          </div>
        </div>
      </div>

      {/* Header Main */}
      <header className="bg-white border-b border-cultured">
        <div className="container mx-auto px-4">
          <div className="flex items-center justify-between py-5 gap-20">
            {/* Logo */}
            <div className="flex-shrink-0">
              <a href="/" className="block">
                <img
                  src="/images/logo/logo.svg"
                  alt="Anon E-commerce"
                  className="h-10 w-auto transition-transform duration-300 hover:scale-105"
                />
              </a>
            </div>

            {/* Search Container */}
            <div className="flex-1 max-w-md">
              <form onSubmit={handleSearch} className="relative">
                <input
                  type="search"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  onFocus={() => setIsSearchFocused(true)}
                  onBlur={() => setIsSearchFocused(false)}
                  placeholder="Search products..."
                  className={`w-full py-2.5 px-4 pr-12 text-onyx text-fs-7 border-2 rounded-border-radius-md transition-all duration-300 outline-none ${
                    isSearchFocused
                      ? 'border-purple-500 shadow-lg transform scale-105 bg-purple-50'
                      : 'border-cultured hover:border-purple-300 hover:shadow-md'
                  }`}
                />
                <button
                  type="submit"
                  className={`absolute right-2 top-1/2 transform -translate-y-1/2 text-onyx text-lg p-2 rounded-border-radius-md transition-all duration-300 ${
                    isSearchFocused
                      ? 'text-purple-600 bg-purple-100 hover:bg-purple-200'
                      : 'hover:text-purple-600 hover:bg-gray-100'
                  }`}
                >
                  <i className={`fas fa-search transition-transform duration-300 ${isSearchFocused ? 'rotate-12' : ''}`}></i>
                </button>
              </form>
            </div>

            {/* Header Actions with Enhanced Animations */}
            <div className="flex items-center gap-4">
              {/* User Account */}
              <button className="relative text-onyx text-2xl p-1.5 rounded-full hover:text-white hover:bg-gradient-to-r hover:from-purple-500 hover:to-blue-500 transition-all duration-300 transform hover:scale-110 hover:-translate-y-1 group">
                <i className="fas fa-user group-hover:rotate-12 transition-transform duration-300"></i>
                <div className="absolute -inset-1 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full opacity-0 group-hover:opacity-20 transition-opacity duration-300"></div>
              </button>

              {/* Wishlist */}
              <button className="relative text-onyx text-2xl p-1.5 rounded-full hover:text-white hover:bg-gradient-to-r hover:from-pink-500 hover:to-red-500 transition-all duration-300 transform hover:scale-110 hover:-translate-y-1 group">
                <i className="fas fa-heart group-hover:animate-pulse"></i>
                <div className="absolute -inset-1 bg-gradient-to-r from-pink-500 to-red-500 rounded-full opacity-0 group-hover:opacity-20 transition-opacity duration-300"></div>
              </button>

              {/* Cart with Badge Animation */}
              <button className="relative text-onyx text-2xl p-1.5 rounded-full hover:text-white hover:bg-gradient-to-r hover:from-green-500 hover:to-teal-500 transition-all duration-300 transform hover:scale-110 hover:-translate-y-1 group">
                <i className="fas fa-shopping-bag group-hover:animate-bounce"></i>
                {itemCount > 0 && (
                  <span className="absolute -top-1 -right-1 bg-gradient-to-r from-red-400 to-pink-500 text-white text-xs font-weight-500 px-1 py-0.5 rounded-full min-w-[18px] text-center animate-pulse shadow-lg">
                    {itemCount}
                  </span>
                )}
                <div className="absolute -inset-1 bg-gradient-to-r from-green-500 to-teal-500 rounded-full opacity-0 group-hover:opacity-20 transition-opacity duration-300"></div>
              </button>

              {/* Mobile Menu Toggle with Animation */}
              <button
                onClick={toggleMobileMenu}
                className="md:hidden text-onyx text-2xl p-1.5 rounded-full hover:bg-gray-100 transition-all duration-300 transform hover:scale-110"
              >
                <i className="fas fa-bars hover:text-purple-600 transition-colors duration-300"></i>
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Desktop Navigation with Enhanced Effects */}
      <nav className="bg-white border-b border-cultured hidden md:block shadow-sm">
        <div className="container mx-auto px-4">
          <ul className="flex justify-center items-center gap-8 py-4">
            {[
              { name: 'Home', href: '/', icon: 'fas fa-home' },
              { name: 'Shop', href: '/shop', icon: 'fas fa-store' },
              { name: 'Categories', href: '/categories', icon: 'fas fa-th-large' },
              { name: 'About', href: '/about', icon: 'fas fa-info-circle' },
              { name: 'Contact', href: '/contact', icon: 'fas fa-envelope' }
            ].map((item, index) => (
              <li key={item.name}>
                <a
                  href={item.href}
                  className="relative text-onyx text-fs-7 font-weight-600 uppercase hover:text-purple-600 transition-all duration-300 transform hover:scale-105 group flex items-center gap-2"
                >
                  <i className={`${item.icon} group-hover:animate-pulse`}></i>
                  <span>{item.name}</span>
                  <div className="absolute -bottom-1 left-0 w-0 h-0.5 bg-gradient-to-r from-purple-500 to-blue-500 group-hover:w-full transition-all duration-300"></div>
                </a>
              </li>
            ))}
          </ul>
        </div>
      </nav>

      <style jsx>{`
        @keyframes slideDown {
          from {
            transform: translateY(-100%);
            opacity: 0;
          }
          to {
            transform: translateY(0);
            opacity: 1;
          }
        }

        .animate-slideDown {
          animation: slideDown 0.6s ease-out;
        }

        /* Enhanced hover effects */
        .group:hover {
          filter: brightness(1.1);
        }

        /* Search input glow effect */
        .search-focused {
          box-shadow: 0 0 20px rgba(147, 51, 234, 0.3);
        }

        /* Floating animation for header top */
        @keyframes float {
          0%, 100% { transform: translateY(0px); }
          50% { transform: translateY(-2px); }
        }

        .animate-float {
          animation: float 3s ease-in-out infinite;
        }
      `}</style>
    </>
  );
};

export default Header;
