import React, { useState, useEffect } from 'react';

const Banner = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const slides = [
    {
      id: 1,
      image: "/api/placeholder/1200/450",
      subtitle: "New Collection",
      title: "Summer Fashion",
      text: "Discover the latest trends in summer fashion with our exclusive collection. Up to 50% off on selected items.",
      buttonText: "Shop Now",
      buttonLink: "/shop"
    },
    {
      id: 2,
      image: "/api/placeholder/1200/450",
      subtitle: "Special Offer",
      title: "Electronics Sale",
      text: "Upgrade your tech with our amazing electronics sale. Premium quality at unbeatable prices.",
      buttonText: "Explore",
      buttonLink: "/electronics"
    },
    {
      id: 3,
      image: "/api/placeholder/1200/450",
      subtitle: "Limited Time",
      title: "Sports & Fitness",
      text: "Gear up for your fitness journey with our premium sports equipment and activewear collection.",
      buttonText: "Get Started",
      buttonLink: "/sports"
    }
  ];

  // Auto-slide functionality
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);

    return () => clearInterval(timer);
  }, [slides.length]);

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  return (
    <section className="banner mt-8 mb-8">
      <div className="container mx-auto px-4">
        <div className="relative overflow-hidden rounded-border-radius-md">
          <div
            className="flex transition-transform duration-500 ease-in-out"
            style={{ transform: `translateX(-${currentSlide * 100}%)` }}
          >
            {slides.map((slide) => (
              <div key={slide.id} className="min-w-full relative">
                <img
                  src={slide.image}
                  alt={slide.title}
                  className="w-full h-96 md:h-[450px] object-cover"
                />
                <div className="absolute inset-0 bg-black bg-opacity-40 flex items-center">
                  <div className="container mx-auto px-4">
                    <div className="max-w-lg text-white">
                      <p className="text-salmon-pink text-fs-7 font-weight-500 uppercase tracking-wider mb-2.5">
                        {slide.subtitle}
                      </p>
                      <h1 className="text-fs-1 font-weight-700 uppercase mb-2.5 leading-tight">
                        {slide.title}
                      </h1>
                      <p className="text-fs-7 font-weight-500 mb-5 hidden md:block">
                        {slide.text}
                      </p>
                      <a
                        href={slide.buttonLink}
                        className="inline-block bg-salmon-pink text-white text-fs-11 font-weight-600 uppercase px-4 py-2 rounded-border-radius-sm hover:bg-eerie-black transition-colors"
                      >
                        {slide.buttonText}
                      </a>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Navigation Dots */}
          <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex gap-2">
            {slides.map((_, index) => (
              <button
                key={index}
                onClick={() => goToSlide(index)}
                className={`w-3 h-3 rounded-full transition-colors ${
                  index === currentSlide
                    ? 'bg-salmon-pink'
                    : 'bg-white bg-opacity-50 hover:bg-opacity-75'
                }`}
              />
            ))}
          </div>

          {/* Navigation Arrows */}
          <button
            onClick={prevSlide}
            className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-white bg-opacity-20 text-white p-2 rounded-full hover:bg-opacity-30 transition-all"
          >
            <i className="fas fa-chevron-left"></i>
          </button>
          <button
            onClick={nextSlide}
            className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-white bg-opacity-20 text-white p-2 rounded-full hover:bg-opacity-30 transition-all"
          >
            <i className="fas fa-chevron-right"></i>
          </button>
        </div>
      </div>
    </section>
  );
};

export default Banner;
