import React, { useState, useEffect } from 'react';
import { useUser } from '../../context/UserContext';

const NewsletterPopup = () => {
  const { showNewsletterPopup, hideNewsletterPopup } = useUser();
  const [email, setEmail] = useState('');
  const [timeLeft, setTimeLeft] = useState(30);
  const [showSuccess, setShowSuccess] = useState(false);
  const [isVisible, setIsVisible] = useState(false);
  const [lastShownTime, setLastShownTime] = useState(0);

  useEffect(() => {
    // Check if we should show the popup (every 30 seconds)
    const checkAndShowPopup = () => {
      const currentTime = Date.now();
      const timeSinceLastShow = currentTime - lastShownTime;

      // Show popup if it's been more than 30 seconds since last show
      if (timeSinceLastShow >= 30000) {
        setIsVisible(true);
        showNewsletterPopup();
        setLastShownTime(currentTime);
        localStorage.setItem('newsletterLastShown', currentTime.toString());
      }
    };

    // Get last shown time from localStorage
    const storedTime = localStorage.getItem('newsletterLastShown');
    if (storedTime) {
      setLastShownTime(parseInt(storedTime));
    }

    // Check immediately on mount
    checkAndShowPopup();

    // Set up interval to check every 10 seconds
    const interval = setInterval(checkAndShowPopup, 10000);

    return () => clearInterval(interval);
  }, [showNewsletterPopup, lastShownTime]);

  useEffect(() => {
    let countdownInterval;
    if (isVisible && timeLeft > 0) {
      countdownInterval = setInterval(() => {
        setTimeLeft((prev) => prev - 1);
      }, 1000);
    }
    return () => clearInterval(countdownInterval);
  }, [isVisible, timeLeft]);

  const handleClose = () => {
    setIsVisible(false);
    hideNewsletterPopup();
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      alert('Please enter a valid email address');
      return;
    }

    // Show success message
    setShowSuccess(true);

    // Create confetti effect
    createConfetti();

    // Reset form
    setEmail('');

    // Hide popup after 2 seconds
    setTimeout(() => {
      handleClose();
      setShowSuccess(false);
    }, 2000);
  };

  const createConfetti = () => {
    const colors = ['#6c5ce7', '#00b894', '#fd79a8', '#fdcb6e', '#00cec9', '#ff7675'];
    const container = document.querySelector('.popup-content');

    if (!container) return;

    for (let i = 0; i < 30; i++) { // Reduced confetti count for better performance
      const confetti = document.createElement('div');
      confetti.className = 'confetti';
      confetti.style.backgroundColor = colors[Math.floor(Math.random() * colors.length)];
      confetti.style.left = Math.random() * 100 + '%';
      confetti.style.top = '-10px';
      confetti.style.width = (Math.random() * 8 + 4) + 'px';
      confetti.style.height = (Math.random() * 8 + 4) + 'px';
      confetti.style.borderRadius = Math.random() > 0.5 ? '50%' : '0';
      confetti.style.position = 'absolute';
      confetti.style.zIndex = '1000';

      container.appendChild(confetti);

      const animationDuration = Math.random() * 2 + 1.5;

      confetti.style.animation = `confettiFall ${animationDuration}s linear forwards`;

      setTimeout(() => {
        if (confetti.parentNode) {
          confetti.remove();
        }
      }, animationDuration * 1000);
    }
  };

  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      handleClose();
    }
  };

  if (!isVisible) return null;

  return (
    <div
      className="newsletter-popup fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-70 backdrop-blur-sm animate-fadeIn p-4"
      onClick={handleBackdropClick}
    >
      <div className="popup-content relative bg-white rounded-2xl shadow-2xl w-full max-w-sm mx-auto overflow-hidden animate-slideUp">
        {/* Floating Shapes - Reduced size and number */}
        <div className="floating-shapes absolute inset-0 overflow-hidden pointer-events-none">
          <div className="shape shape-1 absolute w-12 h-12 bg-purple-500 rounded-full opacity-20 animate-float"></div>
          <div className="shape shape-2 absolute w-8 h-8 bg-green-500 rounded-full opacity-20 animate-float" style={{animationDelay: '2s', animationDuration: '15s'}}></div>
          <div className="shape shape-3 absolute w-6 h-6 bg-pink-500 rounded-full opacity-20 animate-float" style={{animationDelay: '4s', animationDuration: '18s'}}></div>
        </div>

        {/* Close Button */}
        <button
          onClick={handleClose}
          className="close-btn absolute top-3 right-3 w-8 h-8 bg-gray-100 rounded-full flex items-center justify-center text-gray-500 hover:text-red-500 hover:bg-red-50 transition-all duration-300 z-10"
        >
          <i className="fas fa-times text-sm"></i>
        </button>

        {/* Icon */}
        <div className="newsletter-icon text-4xl text-purple-500 mb-3 mt-8 animate-bounce">
          <i className="fas fa-paper-plane"></i>
        </div>

        {/* Title */}
        <h2 className="text-gray-800 text-xl font-bold mb-2 px-6">
          Join Our Newsletter
        </h2>

        {/* Description - Shortened */}
        <p className="text-gray-600 mb-4 px-6 text-sm leading-relaxed">
          Get <span className="font-semibold text-purple-500">10% off</span> your first purchase!
        </p>

        {/* Benefits - Compact version */}
        <div className="benefits mb-4 px-6">
          <div className="flex items-center mb-2 text-gray-600 text-xs">
            <i className="fas fa-check-circle text-green-500 mr-2 text-sm"></i>
            Exclusive discounts
          </div>
          <div className="flex items-center mb-2 text-gray-600 text-xs">
            <i className="fas fa-check-circle text-green-500 mr-2 text-sm"></i>
            Weekly updates
          </div>
          <div className="flex items-center text-gray-600 text-xs">
            <i className="fas fa-check-circle text-green-500 mr-2 text-sm"></i>
            Early access
          </div>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="px-6 pb-6">
          <div className="form-group mb-4">
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
              className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl text-sm bg-gray-50 focus:border-purple-500 focus:bg-white focus:shadow-lg transition-all duration-300 outline-none"
              required
            />
          </div>

          <button
            type="submit"
            className="subscribe-btn w-full bg-gradient-to-r from-purple-500 to-purple-600 text-white font-semibold py-3 px-4 rounded-xl shadow-lg hover:shadow-xl hover:-translate-y-0.5 transition-all duration-300 relative overflow-hidden text-sm"
          >
            <span className="relative z-10">Subscribe Now</span>
          </button>
        </form>

        {/* Countdown - Compact */}
        <div className="countdown text-gray-500 text-xs mb-3 px-6">
          <span className="font-semibold text-purple-500">{timeLeft}</span> seconds left
        </div>

        {/* Progress Bar - Compact */}
        <div className="progress-bar w-full h-1 bg-gray-200 rounded-full mx-6 mb-4 overflow-hidden">
          <div
            className="progress h-full bg-gradient-to-r from-purple-500 to-purple-600 rounded-full transition-all duration-300"
            style={{width: `${((30 - timeLeft) / 30) * 100}%`}}
          ></div>
        </div>

        {/* Success Message */}
        {showSuccess && (
          <div className="success-message text-green-500 font-semibold text-sm mb-4 px-6 animate-fadeIn">
            <i className="fas fa-check-circle mr-1"></i>
            Thank you for subscribing!
          </div>
        )}
      </div>

      <style jsx>{`
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }

        @keyframes slideUp {
          from {
            opacity: 0;
            transform: translateY(30px) scale(0.95);
          }
          to {
            opacity: 1;
            transform: translateY(0) scale(1);
          }
        }

        @keyframes float {
          0% {
            transform: translate(0, 0) rotate(0deg);
          }
          25% {
            transform: translate(5px, 8px) rotate(90deg);
          }
          50% {
            transform: translate(0, 15px) rotate(180deg);
          }
          75% {
            transform: translate(-5px, 8px) rotate(270deg);
          }
          100% {
            transform: translate(0, 0) rotate(360deg);
          }
        }

        @keyframes confettiFall {
          0% {
            opacity: 1;
            transform: translateY(0) rotate(0deg);
          }
          100% {
            opacity: 0;
            transform: translateY(100vh) rotate(720deg);
          }
        }

        .newsletter-popup {
          animation: fadeIn 0.3s ease-out;
        }

        .popup-content {
          animation: slideUp 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
          max-height: 90vh;
          overflow-y: auto;
        }

        .close-btn:hover {
          transform: rotate(90deg);
        }

        .subscribe-btn::after {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background: linear-gradient(135deg, #a363d9 0%, #6c5ce7 100%);
          opacity: 0;
          transition: opacity 0.3s ease;
          z-index: 1;
        }

        .subscribe-btn:hover::after {
          opacity: 1;
        }

        .subscribe-btn span {
          position: relative;
          z-index: 2;
        }

        /* Mobile responsive adjustments */
        @media (max-width: 480px) {
          .popup-content {
            max-width: 95vw;
            margin: 10px;
            padding: 20px 15px;
          }

          .newsletter-icon {
            font-size: 2rem;
            margin-top: 1rem;
          }

          h2 {
            font-size: 1.25rem;
          }

          .benefits {
            margin-bottom: 1rem;
          }

          .form-group input {
            padding: 12px 16px;
            font-size: 14px;
          }

          .subscribe-btn {
            padding: 12px 16px;
            font-size: 14px;
          }
        }
      `}</style>
    </div>
  );
