import React, { useEffect } from 'react';
import { useUser } from '../../context/UserContext';

const Toast = () => {
  const { toast, hideToast } = useUser();

  useEffect(() => {
    if (toast.isOpen) {
      const timer = setTimeout(() => {
        hideToast();
      }, 3000);

      return () => clearTimeout(timer);
    }
  }, [toast.isOpen, hideToast]);

  if (!toast.isOpen) return null;

  const getToastIcon = () => {
    switch (toast.type) {
      case 'success':
        return 'fas fa-check-circle';
      case 'error':
        return 'fas fa-exclamation-circle';
      case 'warning':
        return 'fas fa-exclamation-triangle';
      default:
        return 'fas fa-info-circle';
    }
  };

  const getToastColor = () => {
    switch (toast.type) {
      case 'success':
        return 'bg-ocean-green';
      case 'error':
        return 'bg-bittersweet';
      case 'warning':
        return 'bg-sandy-brown';
      default:
        return 'bg-davys-gray';
    }
  };

  return (
    <div className="notification-toast fixed top-8 right-8 z-50">
      <button
        onClick={hideToast}
        className="toast-close-btn absolute top-2 right-2 text-white text-sm p-1"
      >
        <i className="fas fa-times"></i>
      </button>

      <div className={`toast-banner ${getToastColor()} text-white p-4 rounded-border-radius-md shadow-lg flex items-center gap-3`}>
        <i className={`${getToastIcon()} text-lg`}></i>
        <span className="text-fs-7 font-weight-500">{toast.message}</span>
      </div>
    </div>
  );
};

export default Toast;
