import React, { useState } from 'react';
import { useProducts } from '../../context/ProductContext';

const Sidebar = () => {
  const { categories, updateFilters } = useProducts();
  const [isOpen, setIsOpen] = useState(false);
  const [activeFilter, setActiveFilter] = useState('');

  const handleCategoryFilter = (category) => {
    updateFilters({ category: category === 'all' ? '' : category });
    setActiveFilter(category);
  };

  const handlePriceFilter = (priceRange) => {
    updateFilters({ priceRange });
    setActiveFilter(priceRange);
  };

  const handleRatingFilter = (rating) => {
    updateFilters({ rating });
    setActiveFilter(rating.toString());
  };

  return (
    <aside className="w-full lg:w-80 flex-shrink-0">
      <div className="lg:sticky lg:top-8">
        {/* Sidebar Category */}
        <div className="bg-white border border-cultured rounded-border-radius-md p-6 mb-6">
          <h3 className="text-eerie-black text-lg font-weight-600 uppercase tracking-wider mb-4">
            Categories
          </h3>

          <ul className="space-y-3">
            <li>
              <button
                onClick={() => handleCategoryFilter('all')}
                className={`flex items-center justify-between w-full text-sonic-silver text-base hover:text-salmon-pink transition-colors duration-300 p-2 rounded-border-radius-sm ${
                  activeFilter === 'all' ? 'bg-salmon-pink text-white' : 'hover:bg-cultured'
                }`}
              >
                <span className="flex items-center gap-2">
                  <i className="fas fa-th-large text-sm"></i>
                  All Categories
                </span>
              </button>
            </li>
            {categories.map((category) => (
              <li key={category.id}>
                <button
                  onClick={() => handleCategoryFilter(category.name.toLowerCase())}
                  className={`group flex items-center justify-between w-full text-sonic-silver text-base hover:text-salmon-pink transition-colors duration-300 p-2 rounded-border-radius-sm ${
                    activeFilter === category.name.toLowerCase() ? 'bg-salmon-pink text-white' : 'hover:bg-cultured'
                  }`}
                >
                  <span className="flex items-center gap-2 capitalize">
                    <i className="fas fa-tag text-sm"></i>
                    {category.name}
                  </span>
                  <span className="text-xs bg-cultured text-sonic-silver px-2 py-1 rounded-full">
                    ({category.itemCount})
                  </span>
                </button>
              </li>
            ))}
          </ul>
        </div>

        {/* Price Filter */}
        <div className="bg-white border border-cultured rounded-border-radius-md p-6 mb-6">
          <h3 className="text-eerie-black text-lg font-weight-600 uppercase tracking-wider mb-4">
            Price Range
          </h3>

          <ul className="space-y-3">
            <li>
              <button
                onClick={() => handlePriceFilter('')}
                className={`flex items-center justify-between w-full text-sonic-silver text-base hover:text-salmon-pink transition-colors duration-300 p-2 rounded-border-radius-sm ${
                  activeFilter === '' ? 'bg-salmon-pink text-white' : 'hover:bg-cultured'
                }`}
              >
                <span className="flex items-center gap-2">
                  <i className="fas fa-dollar-sign text-sm"></i>
                  All Prices
                </span>
              </button>
            </li>
            {[
              { range: '0-50', label: '$0 - $50' },
              { range: '50-100', label: '$50 - $100' },
              { range: '100-200', label: '$100 - $200' },
              { range: '200-500', label: '$200+' }
            ].map((price) => (
              <li key={price.range}>
                <button
                  onClick={() => handlePriceFilter(price.range)}
                  className={`group flex items-center justify-between w-full text-sonic-silver text-base hover:text-salmon-pink transition-colors duration-300 p-2 rounded-border-radius-sm ${
                    activeFilter === price.range ? 'bg-salmon-pink text-white' : 'hover:bg-cultured'
                  }`}
                >
                  <span className="flex items-center gap-2">
                    <i className="fas fa-tag text-sm"></i>
                    {price.label}
                  </span>
                </button>
              </li>
            ))}
          </ul>
        </div>

        {/* Rating Filter */}
        <div className="bg-white border border-cultured rounded-border-radius-md p-6 mb-6">
          <h3 className="text-eerie-black text-lg font-weight-600 uppercase tracking-wider mb-4">
            Customer Rating
          </h3>

          <ul className="space-y-3">
            {[5, 4, 3, 2, 1].map((rating) => (
              <li key={rating}>
                <button
                  onClick={() => handleRatingFilter(rating)}
                  className={`group flex items-center gap-2 text-sonic-silver text-base hover:text-salmon-pink transition-colors duration-300 p-2 rounded-border-radius-sm w-full text-left ${
                    activeFilter === rating.toString() ? 'bg-salmon-pink text-white' : 'hover:bg-cultured'
                  }`}
                >
                  <div className="flex">
                    {[1, 2, 3, 4, 5].map((star) => (
                      <i
                        key={star}
                        className={`fas fa-star text-sm ${
                          star <= rating ? 'text-sandy-brown' : 'text-gray-300'
                        }`}
                      ></i>
                    ))}
                  </div>
                  <span className="ml-2">& Up</span>
                </button>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
