import React, { useState, useEffect } from 'react';
import { useUser } from '../../context/UserContext';

const NewsletterPopup = () => {
  const { showNewsletterPopup, hideNewsletterPopup } = useUser();
  const [email, setEmail] = useState('');
  const [timeLeft, setTimeLeft] = useState(30);
  const [showSuccess, setShowSuccess] = useState(false);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // Show popup automatically after 2 seconds
    const timer = setTimeout(() => {
      setIsVisible(true);
      showNewsletterPopup();
    }, 2000);

    return () => clearTimeout(timer);
  }, [showNewsletterPopup]);

  useEffect(() => {
    let countdownInterval;
    if (isVisible && timeLeft > 0) {
      countdownInterval = setInterval(() => {
        setTimeLeft((prev) => prev - 1);
      }, 1000);
    }
    return () => clearInterval(countdownInterval);
  }, [isVisible, timeLeft]);

  const handleClose = () => {
    setIsVisible(false);
    hideNewsletterPopup();
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      alert('Please enter a valid email address');
      return;
    }

    // Show success message
    setShowSuccess(true);

    // Create confetti effect
    createConfetti();

    // Reset form
    setEmail('');

    // Hide popup after 2 seconds
    setTimeout(() => {
      handleClose();
      setShowSuccess(false);
    }, 2000);
  };

  const createConfetti = () => {
    const colors = ['#6c5ce7', '#00b894', '#fd79a8', '#fdcb6e', '#00cec9', '#ff7675'];
    const container = document.querySelector('.popup-content');

    if (!container) return;

    for (let i = 0; i < 50; i++) {
      const confetti = document.createElement('div');
      confetti.className = 'confetti';
      confetti.style.backgroundColor = colors[Math.floor(Math.random() * colors.length)];
      confetti.style.left = Math.random() * 100 + '%';
      confetti.style.top = '-10px';
      confetti.style.width = (Math.random() * 10 + 5) + 'px';
      confetti.style.height = (Math.random() * 10 + 5) + 'px';
      confetti.style.borderRadius = Math.random() > 0.5 ? '50%' : '0';
      confetti.style.position = 'absolute';
      confetti.style.zIndex = '1000';

      container.appendChild(confetti);

      const animationDuration = Math.random() * 3 + 2;

      confetti.style.animation = `confettiFall ${animationDuration}s linear forwards`;

      setTimeout(() => {
        if (confetti.parentNode) {
          confetti.remove();
        }
      }, animationDuration * 1000);
    }
  };

  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      handleClose();
    }
  };

  if (!isVisible) return null;

  return (
    <div
      className="newsletter-popup fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-70 backdrop-blur-sm animate-fadeIn"
      onClick={handleBackdropClick}
    >
      <div className="popup-content relative bg-white rounded-3xl shadow-2xl w-full max-w-md mx-4 p-10 text-center overflow-hidden animate-slideUp">
        {/* Floating Shapes */}
        <div className="floating-shapes absolute inset-0 overflow-hidden pointer-events-none">
          <div className="shape shape-1 absolute w-20 h-20 bg-purple-500 rounded-full opacity-30 animate-float"></div>
          <div className="shape shape-2 absolute w-16 h-16 bg-green-500 rounded-full opacity-30 animate-float" style={{animationDelay: '2s', animationDuration: '15s'}}></div>
          <div className="shape shape-3 absolute w-10 h-10 bg-pink-500 rounded-full opacity-30 animate-float" style={{animationDelay: '4s', animationDuration: '18s'}}></div>
          <div className="shape shape-4 absolute w-12 h-12 bg-yellow-500 rounded-full opacity-30 animate-float" style={{animationDelay: '1s', animationDuration: '25s'}}></div>
        </div>

        {/* Close Button */}
        <button
          onClick={handleClose}
          className="close-btn absolute top-5 right-5 w-10 h-10 bg-gray-100 rounded-full flex items-center justify-center text-gray-500 hover:text-red-500 hover:bg-red-50 transition-all duration-300 z-10"
        >
          <i className="fas fa-times"></i>
        </button>

        {/* Wave Decoration */}
        <div className="wave absolute bottom-0 left-0 w-full h-24 bg-purple-500 opacity-10 rounded-t-full"></div>

        {/* Icon */}
        <div className="newsletter-icon text-6xl text-purple-500 mb-5 animate-bounce">
          <i className="fas fa-paper-plane"></i>
        </div>

        {/* Title */}
        <h2 className="text-gray-800 text-2xl font-bold mb-4">
          Join Our Newsletter
        </h2>

        {/* Description */}
        <p className="text-gray-600 mb-6 leading-relaxed">
          Subscribe to our newsletter and get <span className="font-semibold text-purple-500">10% off</span> your first purchase. Stay updated with our latest news and exclusive offers.
        </p>

        {/* Benefits */}
        <div className="benefits text-left mb-6 px-2">
          <div className="flex items-center mb-3 text-gray-600 hover:translate-x-1 transition-transform">
            <i className="fas fa-check-circle text-green-500 mr-3 text-lg"></i>
            Weekly industry insights
          </div>
          <div className="flex items-center mb-3 text-gray-600 hover:translate-x-1 transition-transform">
            <i className="fas fa-check-circle text-green-500 mr-3 text-lg"></i>
            Exclusive discounts & offers
          </div>
          <div className="flex items-center mb-3 text-gray-600 hover:translate-x-1 transition-transform">
            <i className="fas fa-check-circle text-green-500 mr-3 text-lg"></i>
            Early access to new features
          </div>
          <div className="flex items-center mb-3 text-gray-600 hover:translate-x-1 transition-transform">
            <i className="fas fa-check-circle text-green-500 mr-3 text-lg"></i>
            Premium content & tutorials
          </div>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit}>
          <div className="form-group mb-5">
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email address"
              className="w-full px-5 py-4 border-2 border-gray-200 rounded-2xl text-base bg-gray-50 focus:border-purple-500 focus:bg-white focus:shadow-lg transition-all duration-300 outline-none"
              required
            />
          </div>

          <button
            type="submit"
            className="subscribe-btn w-full bg-gradient-to-r from-purple-500 to-purple-600 text-white font-semibold py-4 px-6 rounded-2xl shadow-lg hover:shadow-xl hover:-translate-y-1 transition-all duration-300 relative overflow-hidden"
          >
            <span className="relative z-10">Subscribe Now</span>
          </button>
        </form>

        {/* Countdown */}
        <div className="countdown text-gray-500 text-sm mt-4">
          <span className="font-semibold text-purple-500">{timeLeft}</span> seconds left to get 10% off
        </div>

        {/* Progress Bar */}
        <div className="progress-bar w-full h-1 bg-gray-200 rounded-full mt-4 overflow-hidden">
          <div
            className="progress h-full bg-gradient-to-r from-purple-500 to-purple-600 rounded-full transition-all duration-300"
            style={{width: `${((30 - timeLeft) / 30) * 100}%`}}
          ></div>
        </div>

        {/* Success Message */}
        {showSuccess && (
          <div className="success-message text-green-500 font-semibold mt-4 animate-fadeIn">
            <i className="fas fa-check-circle mr-2"></i>
            Thank you for subscribing!
          </div>
        )}
      </div>

      <style jsx>{`
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }

        @keyframes slideUp {
          from {
            opacity: 0;
            transform: translateY(50px) scale(0.9);
          }
          to {
            opacity: 1;
            transform: translateY(0) scale(1);
          }
        }

        @keyframes float {
          0% {
            transform: translate(0, 0) rotate(0deg);
          }
          25% {
            transform: translate(10px, 15px) rotate(90deg);
          }
          50% {
            transform: translate(0, 30px) rotate(180deg);
          }
          75% {
            transform: translate(-10px, 15px) rotate(270deg);
          }
          100% {
            transform: translate(0, 0) rotate(360deg);
          }
        }

        @keyframes confettiFall {
          0% {
            opacity: 1;
            transform: translateY(0) rotate(0deg);
          }
          100% {
            opacity: 0;
            transform: translateY(100vh) rotate(720deg);
          }
        }

        .newsletter-popup {
          animation: fadeIn 0.4s ease-out;
        }

        .popup-content {
          animation: slideUp 0.5s cubic-bezier(0.175, 0.885, 0.32, 1.275);
        }

        .close-btn:hover {
          transform: rotate(90deg);
        }

        .subscribe-btn::after {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background: linear-gradient(135deg, #a363d9 0%, #6c5ce7 100%);
          opacity: 0;
          transition: opacity 0.3s ease;
          z-index: 1;
        }

        .subscribe-btn:hover::after {
          opacity: 1;
        }

        .subscribe-btn span {
          position: relative;
          z-index: 2;
        }
      `}</style>
    </div>
  );
};

export default NewsletterPopup;
