import React from 'react';
import { useUser } from '../../context/UserContext';

const Modal = () => {
  const { modal, hideModal } = useUser();

  if (!modal.isOpen) return null;

  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      hideModal();
    }
  };

  const handleNewsletterSubmit = (e) => {
    e.preventDefault();
    // TODO: Implement newsletter subscription
    hideModal();
  };

  return (
    <div className="modal">
      <div className="modal-close-overlay" onClick={handleBackdropClick}></div>

      <div className="modal-content max-w-sm mx-5 bg-white rounded-border-radius-md overflow-hidden">
        <button
          onClick={hideModal}
          className="modal-close-btn absolute top-4 right-4 bg-salmon-pink text-white text-lg p-1.5 rounded-border-radius-sm hover:opacity-90"
        >
          <i className="fas fa-times"></i>
        </button>

        <div className="newsletter p-12 text-center">
          <div className="newsletter-header mb-5">
            <h2 className="newsletter-title text-onyx text-fs-2 font-weight-600 mb-2.5">
              Subscribe Newsletter
            </h2>
            <p className="newsletter-desc text-sonic-silver text-fs-7 leading-relaxed">
              Get the latest updates and exclusive offers directly in your inbox.
            </p>
          </div>

          <form onSubmit={handleNewsletterSubmit}>
            <input
              type="email"
              placeholder="Enter your email address"
              className="email-field text-fs-7 px-4 py-2 border border-cultured rounded-border-radius-sm mb-4"
              required
            />
            <button
              type="submit"
              className="btn-newsletter bg-eerie-black text-white text-fs-7 font-weight-600 uppercase px-4 py-2.5 rounded-border-radius-sm mx-auto hover:bg-salmon-pink transition-colors"
            >
              Subscribe
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Modal;
