import React, { createContext, useContext, useReducer, useEffect } from 'react';
import { useProducts } from './ProductContext';

const CartContext = createContext();

// Cart reducer
const cartReducer = (state, action, products) => {
  switch (action.type) {
    case 'SET_CART':
      return {
        ...state,
        items: action.payload,
        total: calculateTotalWithProducts(action.payload, products),
        itemCount: calculateItemCount(action.payload)
      };
    case 'ADD_ITEM':
      const existingItem = state.items.find(item => item.productId === action.payload.productId);
      let newItems;

      if (existingItem) {
        newItems = state.items.map(item =>
          item.productId === action.payload.productId
            ? { ...item, quantity: item.quantity + action.payload.quantity }
            : item
        );
      } else {
        newItems = [...state.items, {
          id: Date.now().toString(),
          productId: action.payload.productId,
          quantity: action.payload.quantity,
          addedAt: new Date().toISOString()
        }];
      }

      return {
        ...state,
        items: newItems,
        total: calculateTotalWithProducts(newItems, products),
        itemCount: calculateItemCount(newItems)
      };
    case 'UPDATE_ITEM':
      const updatedItems = state.items.map(item =>
        item.id === action.payload.id
          ? { ...item, quantity: action.payload.quantity }
          : item
      ).filter(item => item.quantity > 0);

      return {
        ...state,
        items: updatedItems,
        total: calculateTotalWithProducts(updatedItems, products),
        itemCount: calculateItemCount(updatedItems)
      };
    case 'REMOVE_ITEM':
      const filteredItems = state.items.filter(item => item.id !== action.payload.id);
      return {
        ...state,
        items: filteredItems,
        total: calculateTotalWithProducts(filteredItems, products),
        itemCount: calculateItemCount(filteredItems)
      };
    case 'CLEAR_CART':
      return {
        items: [],
        total: 0,
        itemCount: 0
      };
    case 'SET_USER_INFO':
      return {
        ...state,
        userInfo: { ...state.userInfo, ...action.payload }
      };
    case 'SET_ADDRESS':
      return {
        ...state,
        address: { ...state.address, ...action.payload }
      };
    case 'SET_PAYMENT_METHOD':
      return {
        ...state,
        paymentMethod: action.payload
      };
    case 'SET_CHECKOUT_STEP':
      return {
        ...state,
        currentStep: action.payload
      };
    case 'SET_CHECKOUT_OPEN':
      return {
        ...state,
        isCheckoutOpen: action.payload
      };
    case 'RESET_CHECKOUT':
      return {
        ...state,
        userInfo: {},
        address: {},
        paymentMethod: null,
        currentStep: 1,
        isCheckoutOpen: false
      };
    default:
      return state;
  }
};

// Initial state
const initialState = {
  items: [],
  total: 0,
  itemCount: 0,
  userInfo: {},
  address: {},
  paymentMethod: null,
  currentStep: 1,
  isCheckoutOpen: false
};

// Helper function to calculate total with product lookup
const calculateTotalWithProducts = (items, products) => {
  if (!items || !Array.isArray(items) || items.length === 0) return 0;
  if (!products || !Array.isArray(products) || products.length === 0) return 0;

  return items.reduce((sum, item) => {
    const product = products.find(p => p.id === item.productId);
    if (product && product.price) {
      return sum + (product.price * item.quantity);
    }
    return sum;
  }, 0);
};

// Helper function to calculate item count
const calculateItemCount = (items) => {
  if (!items || !Array.isArray(items) || items.length === 0) return 0;
  return items.reduce((sum, item) => sum + item.quantity, 0);
};

export const CartProvider = ({ children }) => {
  const [state, dispatch] = useReducer((state, action) => cartReducer(state, action, products), initialState);
  const { products } = useProducts();

  // Load cart from localStorage on mount
  useEffect(() => {
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
      try {
        const cartData = JSON.parse(savedCart);
        dispatch({ type: 'SET_CART', payload: cartData });
      } catch (error) {
        console.error('Error loading cart from localStorage:', error);
      }
    }
  }, []);


  // Checkout related functions
  const setUserInfo = (userInfo) => {
    dispatch({ type: 'SET_USER_INFO', payload: userInfo });
  };

  const setAddress = (address) => {
    dispatch({ type: 'SET_ADDRESS', payload: address });
  };

  const setPaymentMethod = (paymentMethod) => {
    dispatch({ type: 'SET_PAYMENT_METHOD', payload: paymentMethod });
  };

  const setCheckoutStep = (step) => {
    dispatch({ type: 'SET_CHECKOUT_STEP', payload: step });
  };

  const openCheckout = () => {
    dispatch({ type: 'SET_CHECKOUT_OPEN', payload: true });
  };

  const closeCheckout = () => {
    dispatch({ type: 'SET_CHECKOUT_OPEN', payload: false });
  };

  const resetCheckout = () => {
    dispatch({ type: 'RESET_CHECKOUT' });
  };

  const nextStep = () => {
    dispatch({ type: 'SET_CHECKOUT_STEP', payload: state.currentStep + 1 });
  };

  const prevStep = () => {
    dispatch({ type: 'SET_CHECKOUT_STEP', payload: state.currentStep - 1 });
  };

  const value = {
    ...state,
    addToCart,
    updateCartItem,
    removeFromCart,
    clearCart,
    setUserInfo,
    setAddress,
    setPaymentMethod,
    setCheckoutStep,
    openCheckout,
    closeCheckout,
    resetCheckout,
    nextStep,
    prevStep
  };

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};
