import React from 'react';
import { useProducts } from '../../context/ProductContext';

const Categories = () => {
  const { categories } = useProducts();

  return (
    <section className="category mb-8">
      <div className="container mx-auto px-4">
        <div className="flex items-center gap-2.5 overflow-x-auto scrollbar-hide">
          {categories.map((category, index) => (
            <div
              key={category.id}
              className="category-item min-w-[calc(25%-15px)] md:min-w-[calc(20%-16px)] flex flex-col items-center gap-2.5 p-4 border-2 border-gray-100 rounded-2xl hover:border-purple-300 transition-all duration-500 cursor-pointer group transform hover:-translate-y-2 hover:shadow-xl"
              style={{
                animationDelay: `${index * 0.1}s`,
                animation: 'fadeInUp 0.6s ease-out forwards'
              }}
            >
              {/* Icon Container with Advanced Effects */}
              <div className="relative w-20 h-20 bg-gradient-to-br from-gray-50 to-gray-100 border-2 border-gray-200 rounded-2xl flex items-center justify-center group-hover:border-purple-400 transition-all duration-500 transform group-hover:scale-110 group-hover:rotate-3 shadow-lg group-hover:shadow-2xl">
                <img
                  src={category.icon}
                  alt={category.name}
                  className="w-10 h-10 text-gray-700 group-hover:brightness-110 transition-all duration-300 transform group-hover:scale-110"
                />

                {/* Floating Elements */}
                <div className="absolute inset-0 opacity-0 group-hover:opacity-100 transition-opacity duration-500">
                  <div className="absolute top-2 right-2 w-3 h-3 bg-yellow-400 rounded-full animate-ping"></div>
                  <div className="absolute bottom-2 left-2 w-2 h-2 bg-pink-400 rounded-full animate-pulse" style={{animationDelay: '0.5s'}}></div>
                  <div className="absolute top-1/2 left-1 w-2 h-2 bg-blue-400 rounded-full animate-bounce" style={{animationDelay: '1s'}}></div>
                </div>

                {/* Glow Effect */}
                <div className="absolute inset-0 bg-gradient-to-r from-purple-400 to-blue-400 rounded-2xl opacity-0 group-hover:opacity-20 transition-opacity duration-500 blur-xl"></div>
              </div>

              {/* Text Content with Animations */}
              <div className="text-center relative">
                <h3 className="text-gray-800 text-fs-9 font-weight-600 uppercase mb-1 transition-all duration-300 group-hover:text-purple-600 transform group-hover:scale-105">
                  {category.name}
                </h3>
                <p className="text-gray-500 text-fs-11 transition-all duration-300 group-hover:text-gray-700 transform group-hover:scale-105">
                  {category.itemCount} items
                </p>

                {/* Decorative Line */}
                <div className="absolute -bottom-1 left-1/2 transform -translate-x-1/2 w-0 h-0.5 bg-gradient-to-r from-purple-500 to-blue-500 group-hover:w-12 transition-all duration-300"></div>
              </div>

              {/* Hover Background Effect */}
              <div className="absolute inset-0 bg-gradient-to-br from-purple-50 to-blue-50 rounded-2xl opacity-0 group-hover:opacity-100 transition-opacity duration-500 -z-10"></div>

              {/* Ripple Effect on Click */}
              <div className="absolute inset-0 rounded-2xl opacity-0 group-active:opacity-100 transition-opacity duration-200">
                <div className="absolute inset-0 bg-purple-200 rounded-2xl animate-ping"></div>
              </div>
            </div>
          ))}
        </div>

        {/* Category Section Title */}
        <div className="text-center mb-8">
          <h2 className="text-3xl font-bold text-gray-800 mb-4 relative inline-block">
            Shop by Category
            <div className="absolute -bottom-2 left-1/2 transform -translate-x-1/2 w-24 h-1 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full"></div>
          </h2>
          <p className="text-gray-600 text-lg">Discover our amazing product categories</p>
        </div>
      </div>

      <style jsx>{`
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .category-item {
          opacity: 0;
          animation: fadeInUp 0.6s ease-out forwards;
        }

        /* Enhanced hover effects */
        .category-item:hover {
          box-shadow: 0 20px 40px rgba(147, 51, 234, 0.15);
        }

        .category-item:hover .w-20 {
          background: linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%);
        }

        /* Icon rotation on hover */
        .category-item:hover img {
          transform: scale(1.1) rotate(5deg);
        }

        /* Floating animation for decorative elements */
        @keyframes float {
          0%, 100% {
            transform: translateY(0px) rotate(0deg);
          }
          50% {
            transform: translateY(-5px) rotate(180deg);
          }
        }

        .category-item:hover .absolute.top-2.right-2 {
          animation: float 2s ease-in-out infinite;
        }

        /* Pulse animation for category items */
        .category-item:hover {
          animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
        }

        /* Scrollbar styling */
        .scrollbar-hide {
          -ms-overflow-style: none;
          scrollbar-width: none;
        }

        .scrollbar-hide::-webkit-scrollbar {
          display: none;
        }

        /* Staggered animation delays */
        .category-item:nth-child(1) { animation-delay: 0.1s; }
        .category-item:nth-child(2) { animation-delay: 0.2s; }
        .category-item:nth-child(3) { animation-delay: 0.3s; }
        .category-item:nth-child(4) { animation-delay: 0.4s; }
        .category-item:nth-child(5) { animation-delay: 0.5s; }
        .category-item:nth-child(6) { animation-delay: 0.6s; }

        /* Mobile responsive adjustments */
        @media (max-width: 768px) {
          .category-item {
            min-width: calc(50% - 10px);
          }

          .category-item .w-20 {
            width: 4rem;
            height: 4rem;
          }

          .category-item img {
            width: 2rem;
            height: 2rem;
          }

          .category-item:hover {
            transform: translateY(-5px);
          }
        }

        @media (max-width: 480px) {
          .category-item {
            min-width: calc(100% - 10px);
            max-width: 200px;
            margin: 0 auto;
          }
        }

        /* Enhanced loading state */
        .category-item::before {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          background: linear-gradient(90deg, transparent, rgba(255,255,255,0.4), transparent);
          transform: translateX(-100%);
          transition: transform 0.6s;
        }

        .category-item:hover::before {
          transform: translateX(100%);
        }
      `}</style>
    </section>
  );
};

export default Categories;
