import React, { useState, useEffect } from 'react';
import { useUser } from '../../context/UserContext';

const NewsletterPopup = () => {
  const { showNewsletterPopup, hideNewsletterPopup } = useUser();
  const [email, setEmail] = useState('');
  const [timeLeft, setTimeLeft] = useState(30);
  const [showSuccess, setShowSuccess] = useState(false);
  const [isVisible, setIsVisible] = useState(false);
  const [lastShownTime, setLastShownTime] = useState(0);
  const [typingText, setTypingText] = useState('');
  const [particles, setParticles] = useState([]);

  useEffect(() => {
    // Check if we should show the popup (every 30 seconds)
    const checkAndShowPopup = () => {
      const currentTime = Date.now();
      const timeSinceLastShow = currentTime - lastShownTime;

      // Show popup if it's been more than 30 seconds since last show
      if (timeSinceLastShow >= 30000) {
        setIsVisible(true);
        showNewsletterPopup();
        setLastShownTime(currentTime);
        localStorage.setItem('newsletterLastShown', currentTime.toString());
        createParticles();
        startTypingAnimation();
      }
    };

    // Get last shown time from localStorage
    const storedTime = localStorage.getItem('newsletterLastShown');
    if (storedTime) {
      setLastShownTime(parseInt(storedTime));
    }

    // Check immediately on mount
    checkAndShowPopup();

    // Set up interval to check every 10 seconds
    const interval = setInterval(checkAndShowPopup, 10000);

    return () => clearInterval(interval);
  }, [showNewsletterPopup, lastShownTime]);

  useEffect(() => {
    let countdownInterval;
    if (isVisible && timeLeft > 0) {
      countdownInterval = setInterval(() => {
        setTimeLeft((prev) => prev - 1);
      }, 1000);
    }
    return () => clearInterval(countdownInterval);
  }, [isVisible, timeLeft]);

  const handleClose = () => {
    setIsVisible(false);
    hideNewsletterPopup();
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      alert('Please enter a valid email address');
      return;
    }

    // Show success message
    setShowSuccess(true);

    // Create confetti effect
    createConfetti();

    // Reset form
    setEmail('');

    // Hide popup after 2 seconds
    setTimeout(() => {
      handleClose();
      setShowSuccess(false);
    }, 2000);
  };

  const createParticles = () => {
    const newParticles = [];
    for (let i = 0; i < 30; i++) {
      const size = Math.random() * 8 + 2;
      const posX = Math.random() * 100;
      const delay = Math.random() * 15;
      const duration = Math.random() * 10 + 10;

      newParticles.push({
        id: i,
        size,
        posX,
        delay,
        duration,
        color: ['#6c5ce7', '#a363d9', '#fd79a8', '#fdcb6e', '#00b894'][Math.floor(Math.random() * 5)]
      });
    }
    setParticles(newParticles);
  };

  const createConfetti = () => {
    const container = document.querySelector('.popup-content');
    const colors = ['#6c5ce7', '#00b894', '#fd79a8', '#fdcb6e', '#00cec9', '#ff7675'];

    if (!container) return;

    for (let i = 0; i < 70; i++) {
      const confetti = document.createElement('div');
      confetti.className = 'confetti';
      confetti.style.backgroundColor = colors[Math.floor(Math.random() * colors.length)];
      confetti.style.left = Math.random() * 100 + '%';
      confetti.style.top = '-10px';
      confetti.style.width = (Math.random() * 12 + 5) + 'px';
      confetti.style.height = (Math.random() * 12 + 5) + 'px';
      confetti.style.borderRadius = Math.random() > 0.5 ? '50%' : '0';
      confetti.style.opacity = '0';
      confetti.style.position = 'absolute';
      confetti.style.zIndex = '1000';

      container.appendChild(confetti);

      const animationDuration = Math.random() * 3 + 2;

      confetti.style.animation = `confettiFall ${animationDuration}s linear forwards`;

      setTimeout(() => {
        if (confetti.parentNode) {
          confetti.remove();
        }
      }, animationDuration * 1000);
    }
  };

  const startTypingAnimation = () => {
    const texts = ["weekly insights", "exclusive offers", "premium content", "early access"];
    let textIndex = 0;
    let charIndex = 0;
    let isDeleting = false;
    let typingSpeed = 100;

    const type = () => {
      const currentText = texts[textIndex];

      if (isDeleting) {
        setTypingText(currentText.substring(0, charIndex - 1));
        charIndex--;
        typingSpeed = 50;
      } else {
        setTypingText(currentText.substring(0, charIndex + 1));
        charIndex++;
        typingSpeed = 100;
      }

      if (!isDeleting && charIndex === currentText.length) {
        isDeleting = true;
        typingSpeed = 1000;
      } else if (isDeleting && charIndex === 0) {
        isDeleting = false;
        textIndex = (textIndex + 1) % texts.length;
        typingSpeed = 500;
      }

      setTimeout(type, typingSpeed);
    };

    type();
  };

  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      handleClose();
    }
  };

  if (!isVisible) return null;

  return (
    <div
      className="newsletter-popup fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-85 backdrop-blur-md animate-fadeIn p-4"
      onClick={handleBackdropClick}
    >
      <div className="popup-content relative bg-gradient-to-br from-white to-gray-50 rounded-3xl shadow-2xl w-full max-w-lg mx-auto overflow-hidden animate-slideUp">
        {/* Floating Shapes */}
        <div className="floating-shapes absolute inset-0 overflow-hidden pointer-events-none">
          <div className="shape shape-1 absolute w-24 h-24 bg-purple-500 rounded-full opacity-40 animate-float" style={{animationDelay: '0s', animationDuration: '25s'}}></div>
          <div className="shape shape-2 absolute w-16 h-16 bg-green-500 rounded-full opacity-40 animate-float" style={{animationDelay: '3s', animationDuration: '20s'}}></div>
          <div className="shape shape-3 absolute w-12 h-12 bg-pink-500 rounded-full opacity-40 animate-float" style={{animationDelay: '6s', animationDuration: '22s'}}></div>
          <div className="shape shape-4 absolute w-14 h-14 bg-yellow-500 rounded-full opacity-40 animate-float" style={{animationDelay: '2s', animationDuration: '28s'}}></div>
        </div>

        {/* Particles */}
        <div className="particles absolute inset-0 pointer-events-none">
        </div>

        {/* Progress Bar - Compact */}
        <div className="progress-bar w-full h-1 bg-gray-200 rounded-full mx-6 mb-4 overflow-hidden">
          <div
            className="progress h-full bg-gradient-to-r from-purple-500 to-purple-600 rounded-full transition-all duration-300"
            style={{width: `${((30 - timeLeft) / 30) * 100}%`}}
          ></div>
        </div>

        {/* Success Message */}
        {showSuccess && (
          <div className="success-message text-green-500 font-semibold text-sm mb-4 px-6 animate-fadeIn">
            <i className="fas fa-check-circle mr-1"></i>
            Thank you for subscribing!
          </div>
        )}
      </div>

      <style jsx>{`
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }

        @keyframes slideUp {
          from {
            opacity: 0;
            transform: translateY(30px) scale(0.95);
          }
          to {
            opacity: 1;
            transform: translateY(0) scale(1);
          }
        }

        @keyframes float {
          0% {
            transform: translate(0, 0) rotate(0deg);
          }
          25% {
            transform: translate(5px, 8px) rotate(90deg);
          }
          50% {
            transform: translate(0, 15px) rotate(180deg);
          }
          75% {
            transform: translate(-5px, 8px) rotate(270deg);
          }
          100% {
            transform: translate(0, 0) rotate(360deg);
          }
        }

        @keyframes confettiFall {
          0% {
            opacity: 1;
            transform: translateY(0) rotate(0deg);
          }
          100% {
            opacity: 0;
            transform: translateY(100vh) rotate(720deg);
          }
        }

        .newsletter-popup {
          animation: fadeIn 0.3s ease-out;
        }

        .popup-content {
          animation: slideUp 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
          max-height: 90vh;
          overflow-y: auto;
        }

        .close-btn:hover {
          transform: rotate(90deg);
        }

        .subscribe-btn::after {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background: linear-gradient(135deg, #a363d9 0%, #6c5ce7 100%);
          opacity: 0;
          transition: opacity 0.3s ease;
          z-index: 1;
        }

        .subscribe-btn:hover::after {
          opacity: 1;
        }

        .subscribe-btn span {
          position: relative;
          z-index: 2;
        }

        /* Mobile responsive adjustments */
        @media (max-width: 480px) {
          .popup-content {
            max-width: 95vw;
            margin: 10px;
            padding: 20px 15px;
          }

          .newsletter-icon {
            font-size: 2rem;
            margin-top: 1rem;
          }

          h2 {
            font-size: 1.25rem;
          }

          .benefits {
            margin-bottom: 1rem;
          }

          .form-group input {
            padding: 12px 16px;
            font-size: 14px;
          }

          .subscribe-btn {
            padding: 12px 16px;
            font-size: 14px;
          }
        }
      `}</style>
    </div>
  );
};

export default NewsletterPopup;
