import React from 'react';

const Footer = () => {
  return (
    <footer className="bg-gradient-to-br from-gray-900 via-purple-900 to-gray-900 py-16 relative overflow-hidden">
      {/* Background Pattern */}
      <div className="absolute inset-0 opacity-10">
        <div className="absolute top-10 left-10 w-32 h-32 bg-purple-500 rounded-full animate-pulse"></div>
        <div className="absolute top-32 right-20 w-24 h-24 bg-blue-500 rounded-full animate-bounce" style={{animationDelay: '1s'}}></div>
        <div className="absolute bottom-20 left-1/4 w-16 h-16 bg-pink-500 rounded-full animate-pulse" style={{animationDelay: '2s'}}></div>
        <div className="absolute bottom-32 right-1/3 w-20 h-20 bg-yellow-500 rounded-full animate-bounce" style={{animationDelay: '0.5s'}}></div>
      </div>

      <div className="container mx-auto px-4 relative z-10">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {/* Company Info with Enhanced Animations */}
          <div className="transform hover:scale-105 transition-all duration-300">
            <h3 className="text-transparent bg-clip-text bg-gradient-to-r from-purple-400 to-pink-400 text-2xl font-bold uppercase mb-4 animate-pulse">
              Anon E-commerce
            </h3>
            <p className="text-gray-300 text-base mb-6 leading-relaxed">
              Your trusted online shopping destination offering quality products at affordable prices with exceptional service.
            </p>
            <div className="flex gap-4">
              {[
                { icon: 'fab fa-facebook', color: 'hover:text-blue-400', delay: '0s' },
                { icon: 'fab fa-twitter', color: 'hover:text-sky-400', delay: '0.1s' },
                { icon: 'fab fa-instagram', color: 'hover:text-pink-400', delay: '0.2s' },
                { icon: 'fab fa-linkedin', color: 'hover:text-blue-300', delay: '0.3s' },
                { icon: 'fab fa-youtube', color: 'hover:text-red-400', delay: '0.4s' }
              ].map((social, index) => (
                <a
                  key={index}
                  href="#"
                  className={`text-gray-400 text-xl p-2 rounded-full bg-gray-800 hover:bg-gray-700 transition-all duration-300 transform hover:scale-110 hover:-translate-y-1 ${social.color}`}
                  style={{animationDelay: social.delay}}
                >
                  <i className={`${social.icon} transition-transform duration-300 hover:rotate-12`}></i>
                </a>
              ))}
            </div>
          </div>

          {/* Quick Links with Hover Effects */}
          <div className="transform hover:scale-105 transition-all duration-300">
            <h4 className="text-white text-lg font-semibold uppercase mb-4 relative">
              Quick Links
              <div className="absolute -bottom-1 left-0 w-12 h-0.5 bg-gradient-to-r from-purple-400 to-pink-400"></div>
            </h4>
            <ul className="space-y-3">
              {[
                { name: 'About Us', href: '/about', icon: 'fas fa-info-circle' },
                { name: 'Contact Us', href: '/contact', icon: 'fas fa-envelope' },
                { name: 'FAQ', href: '/faq', icon: 'fas fa-question-circle' },
                { name: 'Shipping Info', href: '/shipping', icon: 'fas fa-truck' },
                { name: 'Returns', href: '/returns', icon: 'fas fa-undo' }
              ].map((link, index) => (
                <li key={index}>
                  <a
                    href={link.href}
                    className="text-gray-300 text-base hover:text-purple-400 transition-all duration-300 transform hover:translate-x-2 hover:scale-105 flex items-center gap-2 group"
                  >
                    <i className={`${link.icon} text-sm opacity-0 group-hover:opacity-100 transition-opacity duration-300`}></i>
                    <span>{link.name}</span>
                  </a>
                </li>
              ))}
            </ul>
          </div>

          {/* Customer Service with Enhanced Animations */}
          <div className="transform hover:scale-105 transition-all duration-300">
            <h4 className="text-white text-lg font-semibold uppercase mb-4 relative">
              Customer Service
              <div className="absolute -bottom-1 left-0 w-16 h-0.5 bg-gradient-to-r from-blue-400 to-teal-400"></div>
            </h4>
            <ul className="space-y-3">
              {[
                { name: 'My Account', href: '/account', icon: 'fas fa-user' },
                { name: 'Order Tracking', href: '/orders', icon: 'fas fa-search' },
                { name: 'Wishlist', href: '/wishlist', icon: 'fas fa-heart' },
                { name: 'Customer Support', href: '/support', icon: 'fas fa-headset' },
                { name: 'Size Guide', href: '/size-guide', icon: 'fas fa-ruler' }
              ].map((link, index) => (
                <li key={index}>
                  <a
                    href={link.href}
                    className="text-gray-300 text-base hover:text-blue-400 transition-all duration-300 transform hover:translate-x-2 hover:scale-105 flex items-center gap-2 group"
                  >
                    <i className={`${link.icon} text-sm opacity-0 group-hover:opacity-100 transition-opacity duration-300`}></i>
                    <span>{link.name}</span>
                  </a>
                </li>
              ))}
            </ul>
          </div>

          {/* Newsletter with Advanced Effects */}
          <div className="transform hover:scale-105 transition-all duration-300">
            <h4 className="text-white text-lg font-semibold uppercase mb-4 relative">
              Newsletter
              <div className="absolute -bottom-1 left-0 w-14 h-0.5 bg-gradient-to-r from-green-400 to-blue-400"></div>
            </h4>
            <p className="text-gray-300 text-base mb-4 leading-relaxed">
              Subscribe to our newsletter for updates and exclusive offers.
            </p>
            <form className="space-y-3">
              <div className="relative">
                <input
                  type="email"
                  placeholder="Enter your email"
                  className="w-full px-4 py-3 text-gray-800 text-base border-2 border-gray-600 rounded-xl focus:outline-none focus:border-purple-400 focus:bg-gray-800 focus:text-white transition-all duration-300 bg-gray-700"
                />
                <div className="absolute inset-0 bg-gradient-to-r from-purple-400 to-pink-400 rounded-xl opacity-0 hover:opacity-100 transition-opacity duration-300 -z-10"></div>
              </div>
              <button
                type="submit"
                className="w-full bg-gradient-to-r from-purple-500 to-pink-500 text-white text-base font-semibold uppercase py-3 rounded-xl hover:from-purple-600 hover:to-pink-600 transition-all duration-300 transform hover:scale-105 hover:shadow-xl relative overflow-hidden group"
              >
                <span className="relative z-10 flex items-center justify-center gap-2">
                  <i className="fas fa-paper-plane group-hover:animate-bounce"></i>
                  Subscribe
                </span>
                <div className="absolute inset-0 bg-gradient-to-r from-pink-500 to-purple-500 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
              </button>
            </form>
          </div>
        </div>

        {/* Bottom Section with Enhanced Design */}
        <div className="border-t border-gray-700 mt-12 pt-8">
          <div className="flex flex-col md:flex-row justify-between items-center gap-6">
            <div className="flex flex-col md:flex-row items-center gap-4">
              <img
                src="/images/payment.png"
                alt="Payment Methods"
                className="h-10 opacity-80 hover:opacity-100 transition-opacity duration-300 transform hover:scale-105"
              />
              <div className="flex gap-2">
                {['fas fa-credit-card', 'fab fa-paypal', 'fab fa-apple-pay', 'fab fa-google-pay'].map((icon, index) => (
                  <i
                    key={index}
                    className={`${icon} text-2xl text-gray-400 hover:text-purple-400 transition-all duration-300 transform hover:scale-110 hover:-translate-y-1`}
                    style={{animationDelay: `${index * 0.1}s`}}
                  ></i>
                ))}
              </div>
            </div>
            <p className="text-gray-400 text-base font-medium capitalize text-center md:text-right">
              © 2024 Anon E-commerce. All rights reserved. Made with ❤️ for amazing customers.
            </p>
          </div>
        </div>

        {/* Decorative Elements */}
        <div className="absolute bottom-0 left-0 w-full h-1 bg-gradient-to-r from-purple-500 via-pink-500 to-blue-500"></div>
      </div>

      <style jsx>{`
        /* Floating animation for background elements */
        @keyframes float {
          0%, 100% {
            transform: translateY(0px) rotate(0deg);
          }
          50% {
            transform: translateY(-20px) rotate(5deg);
          }
        }

        .absolute.animate-pulse {
          animation: float 6s ease-in-out infinite;
        }

        .absolute.animate-bounce {
          animation: float 4s ease-in-out infinite;
        }

        /* Enhanced hover effects */
        footer a:hover {
          filter: brightness(1.2);
        }

        /* Button ripple effect */
        footer button:active {
          transform: scale(0.95);
        }

        /* Gradient text animation */
        @keyframes gradientShift {
          0% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
          100% { background-position: 0% 50%; }
        }

        .bg-clip-text {
          background: linear-gradient(-45deg, #ee7752, #e73c7e, #23a6d5, #23d5ab);
          background-size: 400% 400%;
          animation: gradientShift 3s ease infinite;
        }

        /* Social media icons hover effect */
        footer .fab:hover {
          transform: rotate(360deg) scale(1.1);
        }

        /* Newsletter form glow effect */
        footer input:focus {
          box-shadow: 0 0 20px rgba(147, 51, 234, 0.5);
        }

        /* Mobile responsive adjustments */
        @media (max-width: 768px) {
          footer {
            padding: 3rem 0;
          }

          .grid-cols-1 {
            gap: 2rem;
          }

          .text-2xl {
            font-size: 1.5rem;
          }
        }

        /* Enhanced scrollbar */
        ::-webkit-scrollbar {
          width: 8px;
        }

        ::-webkit-scrollbar-track {
          background: #1f2937;
        }

        ::-webkit-scrollbar-thumb {
          background: linear-gradient(45deg, #7c3aed, #ec4899);
          border-radius: 4px;
        }

        ::-webkit-scrollbar-thumb:hover {
          background: linear-gradient(45deg, #6d28d9, #db2777);
        }
      `}</style>
    </footer>
  );
};

export default Footer;
